-- Name: present_mod(character varying, character varying, character varying, boolean, boolean, integer, integer, character varying, character varying, boolean); Type: FUNCTION; Schema: mne_builddiary; Owner: admindb
--

CREATE FUNCTION mne_builddiary.present_mod(p_presentid character varying, p_personid character varying, p_timeid character varying, p_present boolean, p_selfpresent boolean, p_typ integer, p_count integer, p_role character varying, p_comment character varying, p_important boolean) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    acttime INT4;
    result varchar;
    r RECORD;
    r_role varchar;
    r_dummy int4;
    r_orderid varchar;
    r_btime int4;
    r_timeid varchar;
BEGIN
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    IF p_presentid = '################' OR p_presentid = '' THEN
        SELECT INTO result mne_catalog.mk_id();
        r_role = p_role;
        
        IF p_role = '' THEN
        SELECT DISTINCT t0.orderid INTO r_orderid FROM mne_builddiary.time t0 WHERE t0.timeid = p_timeid;
       
        SELECT COALESCE(role, ''), MAX(t0.modifydate) INTO r_role, r_dummy
          FROM mne_builddiary.present t0
            LEFT JOIN mne_builddiary.time t1 ON ( t0.timeid = t1.timeid )
          WHERE t0.personid = p_personid AND t1.orderid = r_orderid AND t0.role <> '' AND t0.typ = p_typ
          GROUP BY t0.personid, t1.orderid, t0.role;
          IF r_role IS NULL THEN
            r_role = '';
          END IF;
        END IF;

        IF r_role = '' THEN
        SELECT COALESCE(role, ''), MAX(modifydate) INTO r_role, r_dummy FROM mne_builddiary.present WHERE typ = p_typ AND role <> '' AND personid = p_personid GROUP BY role, personid;
          IF r_role IS NULL THEN
            r_role = '';
          END IF;
        END IF;
                
        IF r_role = '' THEN
          SELECT ( CASE WHEN t1.language = 'en' THEN t0.text_en ELSE t0.text_de END ) INTO r_role FROM mne_application.selectlist t0 LEFT JOIN mne_application.userpref t1 ON ( t1.username = session_user )  where name = 'builddiary_presenttype' and value = CAST ( p_typ as VARCHAR);
        END IF;
        
        FOUND := false;
        SELECT t0.timeid INTO r_timeid 
          FROM mne_personnal.time t0
               LEFT JOIN mne_personnal.orderproducttime t1 ON ( t0.orderproducttimeid = t1.orderproducttimeid )
               LEFT JOIN mne_crm.orderproduct t2 ON ( t2.orderproductid = t1.orderproductid )
               LEFT JOIN mne_builddiary.time t3 ON ( t2.orderid = t3.orderid )
          WHERE t0.userid = p_personid 
            AND (    t3.start >= t0.start               AND t3.start <= t0.start + t0.duration
                  OR t3.start + t3.duration >= t0.start AND t3.start + t3.duration <= t0.start + t0.duration
                  OR t3.start >= t0.start               AND t3.start + t3.duration <= t0.start + t0.duration 
                  OR t3.start <= t0.start               AND t3.start + t3.duration >= t0.start + t0.duration )
            AND t0.userid = p_personid
            AND t3.timeid = p_timeid;

              createdate, createuser, modifydate, modifyuser )
        VALUES
            ( result, p_personid, COALESCE(r_role,''), p_timeid, r_timeid, p_typ, p_comment, p_count, CASE WHEN p_count > 0 THEN p_selfpresent ELSE TRUE END, p_important,
              acttime, session_user, acttime, session_user );		

    ELSEIF p_selfpresent = TRUE OR p_present = TRUE THEN
         SELECT * INTO r FROM mne_builddiary.present  WHERE presentid = p_presentid;

         IF r.timeid <> p_timeid THEN
             SELECT mne_builddiary.present_mod('################', p_personid, p_timeid, TRUE, p_selfpresent, p_typ, p_count, p_role, p_comment, p_important) INTO result;
         ELSEIF p_present = TRUE THEN
             result = p_presentid;
             UPDATE mne_builddiary.present
             SET
                 personid = p_personid,
                 role = p_role,
                 timeid = p_timeid,
                 typ = p_typ,
                 count = p_count,
                 present = p_selfpresent,
                 comment = p_comment,
                 important = p_important,
                 modifydate = acttime,
                 modifyuser = session_user
             WHERE
                 presentid = result;
         ELSE
           DELETE FROM mne_builddiary.present WHERE presentid = p_presentid;
         END IF;
    ELSE
        DELETE FROM mne_builddiary.present WHERE presentid = p_presentid;
    END IF;
    
    return result;
END $$;


