--

CREATE FUNCTION mne_builddiary.time_del(p_timeid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
  r_havebd BOOL;
BEGIN

  SELECT DISTINCT t6.presentid IS NOT NULL INTO r_havebd
  FROM
    mne_personnal.time t0 LEFT JOIN mne_crm.person t1 ON ( t0.userid = t1.personid) 
      LEFT JOIN mne_personnal.orderproducttime t2 ON ( t0.orderproducttimeid = t2.orderproducttimeid)
      LEFT JOIN mne_crm.orderproduct t3 ON ( t2.orderproductid = t3.orderproductid) 
      LEFT JOIN mne_crm.order t4 ON ( t3.orderid = t4.orderid) 
      LEFT JOIN mne_builddiary.time t5 ON ( t4.orderid = t5.orderid AND (t5.start >= t0.start AND t5.start <= t0.start + t0.duration OR t5.start + t5.duration >= t0.start AND t5.start + t5.duration <= t0.start + t0.duration OR t5.start <= t0.start AND t5.start + t5.duration >= t0.start + t0.duration) )
      LEFT JOIN mne_builddiary.present t6 ON ( t6.timeid = t5.timeid AND t1.personid = t6.personid )
  WHERE
    t0.timeid = p_timeid;
    
    PERFORM mne_personnal.time_del(p_timeid);

    UPDATE mne_builddiary.present SET personnaltimeid = NULL 
      WHERE presentid IN ( SELECT presentid from mne_builddiary.present t0 
                             LEFT JOIN mne_personnal.time t1 ON ( t0.personnaltimeid = t1.timeid) WHERE t1.timeid IS NULL AND t0.personnaltimeid IS NOT NULL );
    
    IF r_havebd THEN
      RAISE WARNING '#mne_lang#Bitte Bautagebuch gegebenenfalls korrigieren#';
    END IF;
    
    return 'ok';
END

 $$;


ALTER FUNCTION mne_builddiary.time_del(p_timeid character varying) OWNER TO admindb;

