       return r_timeid;
    END IF;

    SELECT DISTINCT orderid INTO r_orderid
    FROM mne_crm.orderproduct t0 LEFT JOIN mne_personnal.orderproducttime t1 ON ( t0.orderproductid = t1.orderproductid)
    WHERE t1.orderproducttimeid = p_orderproducttimeid;

    IF p_bstart <> 0 AND p_bduration <> 0 THEN
      
      IF r_trecord.bdtimeid IS NULL THEN
            createdate, createuser, modifydate, modifyuser )
        VALUES
          ( mne_catalog.mk_id(), r_orderid, p_bstart, p_bduration, p_weather, p_temperature,
            r_trecord.createdate, session_user, r_trecord.modifydate, session_user );
        
        SELECT * INTO r_trecord FROM mne_builddiary.persontime WHERE timeid = r_timeid;

      ELSE
        UPDATE mne_builddiary.time SET
          orderid = r_orderid,
          start = p_bstart,
          duration = p_bduration,
          weather = p_weather,
          temperature = p_temperature,
          modifydate = r_trecord.modifydate,
          modifyuser = session_user
        WHERE
          timeid = r_trecord.bdtimeid;
      
      END IF;
      
      SELECT * INTO r_precord FROM mne_builddiary.present WHERE timeid = r_trecord.bdtimeid AND personid = p_userid;
      IF r_precord.presentid IS NULL THEN
        SELECT mne_builddiary.present_mod('################', p_userid, r_trecord.bdtimeid, TRUE, TRUE, 1, 0, '', '', false ) AS presentid INTO r_precord;
      END IF;
     
      UPDATE mne_builddiary.present SET personnaltimeid = r_timeid WHERE presentid = r_precord.presentid;
    
    ELSE
       DELETE FROM mne_builddiary.present  WHERE timeid = r_trecord.bdtimeid AND personid =r_trecord.personid;

    END IF;
    
    return r_timeid;
END

 $$;


