CREATE FUNCTION mne_catalog.path(p_tabname character varying, p_id character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$ DECLARE
  str varchar;
  result varchar;
  r RECORD;
  r_id varchar;
  r_count integer;
  r_delimiter varchar;
