    LANGUAGE plpgsql
    AS $$ DECLARE
    stm varchar;
    i integer;
    acttime int4;
BEGIN
  SELECT INTO acttime
      CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  BEGIN
     IF EXISTS ( SELECT FROM   pg_class c JOIN   pg_namespace n ON n.oid = c.relnamespace WHERE  c.relname = indname AND    n.nspname = "schema" ) THEN
