    stm = 'CREATE ' ;
    
    IF isunique THEN
      stm = stm || 'UNIQUE INDEX ';
    ELSE
      stm = stm || 'INDEX ';
    END IF;
    
    IF schema <> '' OR tabname <> '' THEN
      stm = stm || indname || ' ON ' || schema || '.' || tabname;
