    LANGUAGE plpgsql
    AS $$
DECLARE
    rec RECORD;
BEGIN

     SELECT INTO rec count(*) as num 
       FROM
         information_schema.columns
       WHERE
         table_schema = schema
         AND table_name = tabname
         AND column_name in ( 'createdate', 'createuser', 'modifydate', 'modifyuser' );
    
   IF ( rec.num != 4 ) THEN
    RETURN 0;
    ELSE
        RETURN 1;
    END IF;
END;
$$;


