CREATE FUNCTION mne_crm.company_vcard(p_companyid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
r RECORD;
r_uuid VARCHAR;
retval varchar;
BEGIN
  
  SELECT INTO r_uuid uuid FROM mne_catalog.uuid;
  
  SELECT DISTINCT INTO r t0."companyid" AS companyid,
    t0."name" AS name,
    COALESCE( t6.street,'') AS street,
    COALESCE( t6.postbox,'') AS postbox,
    COALESCE( t7.name,'') AS city,
    COALESCE( t7.postcode,'') AS postcode,
    COALESCE( t8.name_de,'') AS country,
    COALESCE( t8.phoneprefix ,'') AS phoneprefix,
    COALESCE( t1."http",'')  AS http,
    COALESCE( t1."telefon",'') AS telefon,
    COALESCE( t1."email",'') AS email,
    COALESCE( t1."mobile",'') AS mobile,
    mne_catalog.epoch_format(t0.modifydate,'YYYY-MM-DD' ) || 'T' || mne_catalog.epoch_format(t0.modifydate,'HH24MI+SS:00' ) as modifydate
  FROM
    mne_crm.company t0 LEFT JOIN mne_crm.companydata t1 ON ( t0.companyid = t1.companydataid)
    LEFT JOIN mne_crm.address t6 ON ( t0.companyid = t6.refid AND t6.addresstypid = '000000000001' )
    LEFT JOIN mne_crm.city t7 ON ( t6.cityid = t7.cityid) 
    LEFT JOIN mne_crm.country t8 ON ( t7.countryid = t8.countryid) 
  WHERE
    ( t0.companyid = p_companyid );

   retval = E'BEGIN:VCARD\r\n'
      ||  E'VERSION:3.0\r\n'
      ||  E'UID:' || r.companyid || E'@erpserver_c_' || r_uuid || E'\r\n'
      ||  E'URI:' || r.companyid || E'@erpserver_c_' || r_uuid || E'\r\n'
      ||  E'REV:' || r.modifydate || E'\r\n'
      ||  E'PRODID:-//Open Source Erp//Persons//EN\r\n'
      ||  E'CATEGORIES:ERP\r\n'
      ||  E'FN:'  || r.name || E'\r\n'
      ||  E'ORG:' || r.name || E'\r\n'
      ||  E'EMAIL;TYPE=WORK:' || r.email || E'\r\n'
      ||  E'TEL;TYPE=WORK:' || r.telefon || E'\r\n'
      ||  E'TEL;TYPE=CELL:' || r.mobile || E'\r\n'
      ||  E'URL;TYPE=WORK:' || r.http || E'\r\n'
      ||  E'ADR;TYPE=WORK:;;' || r.street || ';' || r.city || ';;' || r.postcode || ';' || r.country || E'\r\n'
      ||  E'NOTE:\r\n'
      ||  E'END:VCARD\r\n';

    return retval;
END $$;


