CREATE FUNCTION mne_crm.offer_add(p_offerid character varying, add_offerid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ 
DECLARE 
    acttime INTEGER;
    r mne_crm.offerproduct%ROWTYPE;
BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    FOUND := FALSE;
    PERFORM offerid
    FROM mne_crm.offer t0 LEFT JOIN mne_crm.offerprobability t1 ON ( t0.offernumber = t1.offernumber )
    WHERE t1.probability IN ( 0, 100 ) AND t0.offerid = p_offerid;
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Das Angebot ist geschlossen#';
    END IF;

    FOR r IN SELECT * from mne_crm.offerproduct WHERE offerid = add_offerid LOOP
        SELECT mne_crm.offer_add_product_add(p_offerid, r, acttime) INTO r.offerproductid;

        UPDATE mne_crm.offerproduct SET productcost = mne_crm.offerproduct_cost(offerproductid)
        WHERE offerproductid = r.offerproductid AND productcostrecalc = true;
    END LOOP;
    

   return p_offerid;
END;
 $$;


