--
-- Name: offer_add_product_add(character varying, mne_crm.offerproduct, integer); Type: FUNCTION; Schema: mne_crm; Owner: admindb
--

CREATE FUNCTION mne_crm.offer_add_product_add(p_offerid character varying, p mne_crm.offerproduct, acttime integer) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    r mne_crm.offerproduct%ROWTYPE;
    t mne_personnal.offerproducttime%ROWTYPE;
    m mne_warehouse.offerproductpart%ROWTYPE;
    
BEGIN
    SELECT * from mne_crm.offerproduct INTO r WHERE productname = p.productname and offerproducttype = p.offerproducttype AND offerid = p_offerid;
    IF FOUND THEN
        UPDATE mne_crm.offerproduct SET count = count + p.count WHERE offerproductid = r.offerproductid;  
    ELSE
        r = p;
        SELECT mne_catalog.mk_id() INTO r.offerproductid;
        SELECT mne_crm.offerproduct_mknumber(p_offerid, r.productnumber ) INTO r.productnumber;
        SELECT COALESCE(MAX(position),0) + 1 INTO r.position FROM mne_crm.offerproduct WHERE offerid = p_offerid;
        r.offerid = p_offerid;
    END IF;
    
    FOR t IN SELECT * from mne_personnal.offerproducttime WHERE offerproductid = p.offerproductid LOOP
        FOUND := false;
        PERFORM offerproducttimeid FROM mne_personnal.offerproducttime
        WHERE offerproductid = r.offerproductid AND description = t.description;
        IF NOT FOUND THEN
          SELECT mne_catalog.mk_id() INTO t.offerproducttimeid;
          t.offerproductid = r.offerproductid;
        END IF;
    END LOOP;
    
    FOR m IN SELECT * FROM mne_warehouse.offerproductpart WHERE offerproductid = p.offerproductid LOOP
      FOUND := FALSE;
      PERFORM offerproductpartid FROM mne_warehouse.offerproductpart
      WHERE offerproductid = r.offerproductid AND partdescription = m.partdescription;
      IF NOT FOUND THEN
        SELECT mne_catalog.mk_id() INTO m.offerproductpartid;
        m.offerproductid = r.offerproductid;
      END IF;
    END LOOP;
    
    return r.offerproductid;
END;
 $$;


