CREATE FUNCTION mne_crm.offer_copy(p_offerid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    r mne_crm.offerproduct%ROWTYPE;
    acttime INTEGER;
    newid TEXT;
    p_refid TEXT;
    p_offernumber TEXT;
    p_offerproductid TEXT;
    p_version INTEGER;
BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    FOUND := FALSE;
    PERFORM offerid
    FROM mne_crm.offer t0 LEFT JOIN mne_crm.offerprobability t1 ON ( t0.offernumber = t1.offernumber )
    WHERE t1.probability IN ( 0, 100 ) AND t0.offerid = p_offerid;
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Das Angebot ist geschlossen#';
    END IF;

    SELECT INTO newid mne_catalog.mk_id();

    /* Tabelle offer füllen */
    /* ==================== */

    SELECT refid, offernumber, max(version) + 1 INTO p_refid, p_offernumber, p_version
        FROM mne_crm.offer WHERE offernumber = ( SELECT offernumber FROM mne_crm.offer where offerid = p_offerid )
                           GROUP BY refid, offernumber;
    
    EXECUTE 'INSERT INTO mne_crm.offer '
         || '( offerid, refid, ownerid, contactid, offernumber, '
         || '  description, version, text, withvat, '
         || '  createdate, createuser, modifydate, modifyuser ) '
         || 'SELECT '
         ||    quote_literal(newid) 
         || ' , refid, ownerid, contactid, offernumber, '
         || '   description,' || quote_literal(p_version) || ', text, withvat, '
         || quote_literal(acttime)
         || ', session_user, '
         || quote_literal(acttime)
         || ', session_user '
         || 'FROM '
         || 'mne_crm.offer '
         || 'WHERE '
         || 'offerid = ' || quote_literal(p_offerid);


    BEGIN
            createdate, createuser, modifydate, modifyuser )
        SELECT 
            newid,ansatz,besonders,kosten,l1,l2,l3,l4,l5,l6,l7,l8,l9,law,leistung,neben,zeit,zone,zusatz,zuschlag,pauschal,
            createdate, createuser, acttime, session_user
        FROM 
            mne_hoai.offer
        WHERE
             offerid = p_offerid;
    EXCEPTION WHEN invalid_schema_name THEN
        -- do nothing,
    END;

    /* Tabelle offerproduct füllen */
    /* ==================== */

    FOR r in SELECT * from mne_crm.offerproduct WHERE offerid = p_offerid LOOP
      SELECT INTO p_offerproductid mne_catalog.mk_id();
      
      r.createdate = acttime;
      r.createuser = session_user;
      r.modifydate = acttime;
      r.modifyuser = session_user;
      r.offerproductid = p_offerproductid;
      r.offerid = newid;
      
        
      EXECUTE 'INSERT INTO mne_personnal.offerproducttime '
           || '( offerproducttimeid, offerproductid, setduration, skillid, step, longdesc, description, '
           || '  createdate, createuser, modifydate, modifyuser ) '
           || 'SELECT '
           || '  mne_catalog.mk_id(),'
           ||    quote_literal(p_offerproductid) 
           || ', setduration, skillid, step, longdesc, description,'
           || quote_literal(acttime)
           || ', session_user, '
           || quote_literal(acttime)
           || ', session_user '
           || 'FROM '
           || 'mne_personnal.offerproducttime '
           || 'WHERE '
           || 'offerproductid = ' || quote_literal(r.offerproductid);

      EXECUTE 'INSERT INTO mne_warehouse.offerproductpart '
           || '( offerproductpartid, offerproductid, count, partcost, partgroup, partdescription, '
           || '  createdate, createuser, modifydate, modifyuser ) '
           || 'SELECT '
           || '  mne_catalog.mk_id(),'
           ||    quote_literal(p_offerproductid) 
           || ', count, partcost, partgroup, partdescription, '
           || quote_literal(acttime)
           || ', session_user, '
           || quote_literal(acttime)
           || ', session_user '
           || 'FROM '
           || 'mne_warehouse.offerproductpart '
           || 'WHERE '
           || 'offerproductid = ' || quote_literal(r.offerproductid);

    END LOOP;
        
    return newid;
END;
 $$;


