CREATE FUNCTION mne_crm.offer_del(p_offerid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
BEGIN

    DELETE FROM mne_warehouse.offerproductpart
    WHERE offerproductid IN ( SELECT offerproductid FROM mne_crm.offerproduct WHERE offerid = p_offerid );
    
    DELETE FROM mne_personnal.offerproducttime
    WHERE offerproductid IN ( SELECT offerproductid FROM mne_crm.offerproduct WHERE offerid = p_offerid );

    BEGIN
        DELETE FROM mne_hoai.offer WHERE offerid = p_offerid;
    EXCEPTION WHEN invalid_schema_name OR undefined_table THEN
        -- do nothing,
    END;

    DELETE FROM mne_crm.file WHERE secondrefid = p_offerid;
    DELETE FROM mne_crm.offerproduct WHERE offerid = p_offerid;
    DELETE FROM mne_crm.offer WHERE offerid = p_offerid;
    
    DELETE FROM mne_crm.offerprobability
    WHERE offernumber NOT IN ( SELECT DISTINCT offernumber from mne_crm.offer );
         
    RETURN 'ok';
END $$;


