         || '( orderproductpartid, orderproductid, actcount, count, partcost, partcostcalc, partgroup, partdescription, partid, '
         || '  createdate, createuser, modifydate, modifyuser ) '
         || 'SELECT mne_catalog.mk_id(), t0.orderproductid, 0, t2.count, t2.partcost, t2.partcost, t2.partgroup, t2.partdescription, t2.partid, '
         ||          to_char(acttime, '9999999999999999') || ', '
         || '        session_user, '
         ||         to_char(acttime, '99999999999999999') || ', '
         || '        session_user '
         || 'FROM mne_crm.orderproduct t0 '
         || '    LEFT JOIN mne_crm.product t1 ON ( t0.productid = t1.productid ) '
         || '    LEFT JOIN mne_warehouse.productpart t2 ON ( t2.productid = t1.productid ) '
         || '    LEFT JOIN mne_crm.offerproduct t3 ON ( t3.offerproductid = t0.offerproductid ) '
         || '    LEFT JOIN mne_warehouse.offerproductpart t4 ON ( t4.offerproductid = t3.offerproductid ) '
         || 'WHERE t4.offerproductid IS NULL AND t2.productpartid IS NOT NULL AND t0.orderid = ' || quote_literal(r_orderid);
 
    EXECUTE 'INSERT INTO mne_warehouse.orderproductpart '
         || '( orderproductpartid, orderproductid, actcount, count, partcost, partcostcalc, partgroup, partdescription, partid,'
         || '  createdate, createuser, modifydate, modifyuser ) '
         || 'SELECT mne_catalog.mk_id(), t1.orderproductid, 0, t0.count, t0.partcost, t0.partcost, t0.partgroup, t0.partdescription, t0.partid, '
         ||          to_char(acttime, '9999999999999999') || ', '
         || '        session_user, '
         ||         to_char(acttime, '99999999999999999') || ', '
         || '        session_user '
         || 'FROM mne_warehouse.offerproductpart t0 '
         || '    LEFT JOIN mne_crm.orderproduct t1 ON ( t0.offerproductid = t1.offerproductid ) '
         || '    LEFT JOIN mne_crm.offerproduct t2 ON ( t2.offerproductid = t0.offerproductid ) '
         || 'WHERE t2.offerid = '|| quote_literal(p_offerid);
 
    /* Dokumente transferieren  */
    /* ======================== */
    UPDATE mne_crm.file SET secondrefid = r_orderid WHERE secondrefid = p_offerid;

    UPDATE mne_crm.offerprobability SET probability = 100 WHERE offernumber = p_offernumber;
    
    FOR lrecord IN SELECT offerid FROM mne_crm.offer WHERE offernumber = p_offernumber AND offerid <> p_offerid LOOP
      PERFORM mne_crm.offer_del(lrecord.offerid);
    END LOOP;
    
    return r_orderid;
END; $$;


