--
-- Name: offerproduct_add(character varying, character varying, character varying); Type: FUNCTION; Schema: mne_crm; Owner: admindb
--

CREATE FUNCTION mne_crm.offerproduct_add(p_offerid character varying, p_offerproducttype character varying, p_productid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    p_offerproductid VARCHAR;
    p_position INTEGER;
    p_currencyid VARCHAR;
    r_productnumber VARCHAR;
    r_i INTEGER;
    r RECORD;
 BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    SELECT * INTO r FROM mne_crm.product t0 WHERE t0.productid = p_productid;
    IF NOT FOUND THEN
      RAISE EXCEPTION '#mne_lang#Das Produkt ist unbekannt#';
    END IF;

    r_i := -1;
    SELECT MAX(position) + 1 INTO p_position FROM mne_crm.offerproduct WHERE offerid = p_offerid;
    IF p_position IS NULL THEN
      p_position = 0;
      r_i = 0;
    END IF;

    IF r.productnumber = '' OR r.productnumber IS NULL THEN
      LOOP
        PERFORM productnumber FROM mne_crm.offerproduct WHERE offerid = p_offerid AND productnumber = CAST( (p_position + r_i ) AS varchar);
        EXIT WHEN NOT FOUND;
        r_i := r_i + 1;
      END LOOP;
      r_productnumber = CAST( (p_position + r_i ) AS varchar );
    ELSE
      r_productnumber = r.productnumber;
    END IF;

    FOUND := FALSE;
    PERFORM offerproductid
    FROM mne_crm.offerproduct t0 LEFT JOIN mne_crm.product t1 ON ( t0.productname = t1.name AND t1.productnumber != '' )
    WHERE t1.productid = p_productid AND t0.offerid = p_offerid;
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Es existiert schon ein Produkt mit gleichem Namen#';
    END IF;
    
    FOUND := FALSE;
    PERFORM offerid
    FROM mne_crm.offer t0 LEFT JOIN mne_crm.offerprobability t1 ON ( t0.offernumber = t1.offernumber )
    WHERE t1.probability IN ( 0, 100 ) AND t0.offerid = p_offerid;
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Das Angebot ist geschlossen#';
    END IF;

    FOUND := FALSE;
    PERFORM offerid
    FROM mne_crm.offerproduct t0 LEFT JOIN mne_crm.product t1 ON ( t0.productnumber = t1.productnumber AND t1.productnumber != '' )
    WHERE t1.productid = p_productid AND t0.offerid = p_offerid;
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Es existiert schon ein Produkt mit gleicher Produktnummer#';
    END IF;

    
    SELECT DISTINCT currencyid INTO p_currencyid
    FROM mne_base.currency c, mne_crm.companyown o
    WHERE c.currency = o.currency;
    
    SELECT INTO p_offerproductid mne_catalog.mk_id();
      count,
      offerid,
      offerproductid,
      offerproducttype,
      withworkingstep,
      position,
      productid,
      productoptid,
      productcurrencyid,
      productcurrencyrate,
      productdescription,
      productname,
      productnumber,
      productprice,
      productcost,
      productcostrecalc,
      productunit,
      productvat,
      createdate, createuser, modifydate, modifyuser )
      SELECT
        CASE WHEN t1.unit = '%' THEN 100 ELSE 1 END,
        p_offerid,
        p_offerproductid,
        p_offerproducttype,
        t0.withworkingstep,
        p_position,
        CASE WHEN NULLIF(t0.productnumber,'') IS NULL THEN NULL ELSE p_productid END,
        CASE WHEN NULLIF(t0.productnumber,'') IS NULL THEN NULL ELSE p_productid END,
        COALESCE(NULLIF(t1.currencyid,''), p_currencyid),
        t2.rate_purchase,
        t0.description,
        CASE WHEN NULLIF(t0.productnumber,'') IS NULL THEN t0.name || ' ' || r_productnumber ELSE t0.name END,
        r_productnumber,
        COALESCE(t1.unitprice,0),
        COALESCE(t1.unitcost,0),
        true,
        t1.unit,
        t1.vat,
        acttime, session_user, acttime, session_user
    FROM 
         ( mne_crm.product t0 LEFT JOIN mne_crm.productprice t1 ON ( t0.productid = t1.productid ))
         LEFT JOIN mne_base.currency t2 ON ( t1.currencyid = t2.currencyid )
        WHERE
          t0.productid = p_productid;

    RETURN p_offerproductid;
END

 $$;


