--
-- Name: offerproduct_cost(character varying); Type: FUNCTION; Schema: mne_crm; Owner: admindb
--

CREATE FUNCTION mne_crm.offerproduct_cost(p_offerproductid character varying) RETURNS double precision
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    result float8;
    f float8;
BEGIN
    
  SELECT
    SUM(price)
  INTO
    result
  FROM
    (
    SELECT
      offerproductpartid, COALESCE(t1.count * t1.partcost, t3.count * t3.partcost, 0.0) AS price
    FROM 
      mne_crm.offerproduct t0
      left join mne_warehouse.offerproductpart t1 ON ( t0.offerproductid = t1.offerproductid )
      left join mne_crm.product t2 ON ( t0.productid = t2.productid AND t1.offerproductid IS NULL )
      left join mne_warehouse.productpart t3 ON ( t2.productid = t3.productid )
    WHERE
      t0.offerproductid = p_offerproductid
    ) tt0;
  
  SELECT
    SUM(price )
  INTO
    f
  FROM 
    (
    SELECT
      offerproducttimeid, COALESCE((t1.setduration / 3600.0) * t2.unitcost, ( t4.duration / 3600.0) * t5.unitcost, 0.0) as price
    FROM 
      mne_crm.offerproduct t0
      left join mne_personnal.offerproducttime t1 ON ( t0.offerproductid = t1.offerproductid )
      left join mne_personnal.skill t2 ON ( t1.skillid = t2.skillid )
      left join mne_crm.product t3 ON ( t0.productid = t3.productid AND t1.offerproductid IS NULL )
      left join mne_personnal.producttime t4 ON ( t3.productid = t4.productid )
      left join mne_personnal.skill t5 ON ( t4.skillid = t5.skillid )
    WHERE
      t0.offerproductid = p_offerproductid
    ) as tt0;
    
  result = result + f;
  
  return result;

END $$;


