-- Name: offerproduct_ok(character varying, character varying, boolean, integer, character varying, character varying, character varying, double precision, character varying, character varying, character varying, double precision, double precision, double precision, boolean, character varying, double precision); Type: FUNCTION; Schema: mne_crm; Owner: admindb
--

CREATE FUNCTION mne_crm.offerproduct_ok(p_offerproductid character varying, p_offerproducttype character varying, p_withworkingstep boolean, p_position integer, p_productid character varying, p_productoptid character varying, p_productcurrencyid character varying, p_productcurrencyrate double precision, p_productdescription character varying, p_productname character varying, p_productnumber character varying, p_count double precision, p_productprice double precision, p_productcost double precision, p_productcostrecalc boolean, p_productunit character varying, p_productvat double precision) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    r RECORD;
    r_productid VARCHAR;
 BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    SELECT t0.productid, t0.productdescription, t0.productname, t2.probability INTO r FROM mne_crm.offerproduct t0
                         LEFT JOIN mne_crm.offer t1 ON ( t0.offerid = t1.offerid )
                         LEFT JOIN mne_crm.offerprobability t2 ON ( t1.offernumber = t2.offernumber )
    WHERE t0.offerproductid = p_offerproductid;
    
    IF r.probability = 0 OR r.probability = 100 THEN
        RAISE EXCEPTION '#mne_lang#Das Angebot ist geschlossen#';
    END IF;
    
    r_productid = NULLIF(p_productid, '');
    IF r.productdescription <> p_productdescription OR r.productname <> p_productname THEN
      r_productid = NULL;

      PERFORM * FROM mne_personnal.offerproducttime WHERE offerproductid = p_offerproductid;
      IF NOT FOUND THEN
          createdate, createuser, modifydate, modifyuser )
        SELECT mne_catalog.mk_id(), p_offerproductid, description, duration, skillid, step,
             acttime, session_user, acttime, session_user
        FROM mne_personnal.producttime
        WHERE productid = ( select productid FROM mne_crm.offerproduct WHERE offerproductid = p_offerproductid );
      END IF;
      
      PERFORM * FROM mne_warehouse.offerproductpart WHERE offerproductid = p_offerproductid;
      IF NOT FOUND THEN
          createdate, createuser, modifydate, modifyuser )
        SELECT mne_catalog.mk_id(), p_offerproductid, count, partcost, partdescription, partid, partgroup,
          acttime, session_user, acttime, session_user
        FROM mne_warehouse.productpart
        WHERE productid = ( select productid FROM mne_crm.offerproduct WHERE offerproductid = p_offerproductid );
      END IF;
    END IF;
                         
    UPDATE mne_crm.offerproduct
    SET
      count = p_count,
      offerproducttype = p_offerproducttype,
      withworkingstep = p_withworkingstep,
      position = p_position,
      productid = r_productid,
      productoptid = p_productoptid,
      productcurrencyid = p_productcurrencyid,
      productcurrencyrate = p_productcurrencyrate,
      productdescription = p_productdescription,
      productname = p_productname,
      productnumber = p_productnumber,
      productprice = p_productprice,
      productcost = p_productcost,
      productcostrecalc = p_productcostrecalc,
      productunit = p_productunit,
      productvat = p_productvat,
      modifydate = acttime,
      modifyuser = session_user
    WHERE
      offerproductid = p_offerproductid;

    PERFORM mne_personnal.offerproducttime_calc(p_offerproductid);
    
    RETURN p_offerproductid;
END

 $$;


