-- Name: offerproduct_standard(character varying); Type: FUNCTION; Schema: mne_crm; Owner: admindb
--

CREATE FUNCTION mne_crm.offerproduct_standard(p_offerid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
  r RECORD;
  acttime INTEGER;
  pp_unit VARCHAR;
  pp_currencyid VARCHAR;
  pp_description VARCHAR;
  pp_name VARCHAR;
  pp_count FLOAT8;
  pp_unitprice FLOAT8;
  pp_unitcost FLOAT8;
  pp_rate_purchase FLOAT8;
  pp_vat FLOAT8;

BEGIN
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
    
    FOUND := FALSE;
    PERFORM offerid
    FROM mne_crm.offer t0 LEFT JOIN mne_crm.offerprobability t1 ON ( t0.offernumber = t1.offernumber )
    WHERE t1.probability IN ( 0, 100 ) AND t0.offerid = p_offerid;
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Das Angebot ist geschlossen#';
    END IF;

    FOR r in SELECT * FROM mne_crm.offerproduct WHERE offerid = p_offerid AND productid IS NOT NULL
    LOOP
    
    SELECT
        CASE WHEN t1.unit = '%' THEN 100 ELSE 1 END,
        COALESCE(NULLIF(t1.currencyid,''), r.productcurrencyid),
        t2.rate_purchase,
        t0.description,
        t0.name,
        COALESCE(t1.unitprice,0),
        COALESCE(t1.unitcost,0),
        t1.unit,
        t1.vat
    INTO
        pp_count, pp_currencyid, pp_rate_purchase, pp_description, pp_name,
        pp_unitprice, pp_unitcost, pp_unit, pp_vat
    FROM 
         mne_crm.product t0 LEFT JOIN mne_crm.productprice t1 ON ( t0.productid = t1.productid )
         LEFT JOIN mne_base.currency t2 ON ( t1.currencyid = t2.currencyid )
        WHERE
          t0.productid = r.productid;
          
    UPDATE mne_crm.offerproduct 
    SET
    (
      productcurrencyid, productcurrencyrate, productdescription, productname,
      productprice, productcost, productunit, productvat,
      modifydate, modifyuser
    )
    =
    ( pp_currencyid, pp_rate_purchase, pp_description, pp_name,
      pp_unitprice, pp_unitcost, pp_unit, pp_vat, acttime, session_user
    )
    WHERE offerproductid = r.offerproductid;
  END LOOP;  
    RETURN 'ok';
END

 $$;


