--
-- Name: offerproduct_update(character varying, character varying, character varying, double precision, double precision, double precision); Type: FUNCTION; Schema: mne_crm; Owner: admindb
--

CREATE FUNCTION mne_crm.offerproduct_update(p_offerproductid character varying, p_name character varying, p_number character varying, p_count double precision, p_price double precision, p_cost double precision) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    r RECORD;
 BEGIN
    
    SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
    SELECT * INTO r from mne_crm.offerproduct WHERE offerproductid = p_offerproductid;

    FOUND := FALSE;
    PERFORM offerid
    FROM mne_crm.offer t0 LEFT JOIN mne_crm.offerprobability t1 ON ( t0.offernumber = t1.offernumber )
    WHERE t1.probability IN ( 0, 100 ) AND t0.offerid = r.offerid;
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Das Angebot ist geschlossen#';
    END IF;

    IF NOT r.productid IS NULL THEN
        SELECT * INTO r FROM mne_crm.product WHERE productid = r.productid;
        IF r.name != p_name OR r.productnumber != p_number THEN
            r.productid = NULL;
        END IF;
    END IF;
    
    UPDATE mne_crm.offerproduct SET
      count = p_count,
      productprice = p_price,
      productname = p_name,
      productnumber = p_number,
      productid = r.productid,
      productcost = p_cost,
      modifydate = acttime,
      modifyuser = session_user
    WHERE
      offerproductid = p_offerproductid;

    RETURN p_offerproductid;
END

 $$;


