    AS $$ DECLARE 
BEGIN
    
   FOUND := false;
   PERFORM orderid FROM mne_shipment.deliverynote WHERE orderid = p_orderid;
   IF FOUND THEN
       RAISE EXCEPTION '#mne_lang#Es wurden Teile der Bestellung ausgeliefert#';
   END IF;

