   PERFORM t0.orderproducttimeid FROM mne_personnal.time t0
                          LEFT JOIN mne_personnal.orderproducttime t1 ON ( t1.orderproducttimeid = t0.orderproducttimeid )
                          LEFT JOIN mne_crm.orderproduct t2 ON ( t1.orderproductid = t2.orderproductid )
   WHERE t2.orderid = p_orderid;
   IF FOUND THEN
       RAISE EXCEPTION '#mne_lang#Es wurde am Auftrag gearbeitet#';
   END IF;

   DELETE FROM mne_warehouse.orderproductpart
   WHERE orderproductid IN ( SELECT orderproductid
                             FROM mne_crm.orderproduct
                             WHERE orderid = p_orderid );
   DELETE FROM mne_personnal.orderproducttime
   WHERE orderproductid IN ( SELECT orderproductid
                             FROM mne_crm.orderproduct
                             WHERE orderid = p_orderid );
                             
   BEGIN
      DELETE FROM mne_hoai.order       WHERE orderid = p_orderid;
   EXCEPTION WHEN invalid_schema_name OR undefined_table THEN
      -- do nothing
   END;
   DELETE FROM mne_crm.file         WHERE secondrefid = p_orderid;
   DELETE FROM mne_crm.orderproduct WHERE orderid = p_orderid;
   DELETE FROM mne_crm.order WHERE orderid = p_orderid;
   
   return 'ok';
END;
 $$;


