--

CREATE FUNCTION mne_crm.order_deliver(p_orderid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ 
DECLARE 
    acttime INTEGER;
    p_deliverynoteid TEXT;
    p_deliverynotenumber INTEGER;
    p_num INTEGER;
    p_ownerid TEXT;
    p_contactid TEXT;
    p_refid TEXT;
    p_open BOOLEAN;
    p_closed BOOLEAN;
    i INTEGER;
    p_last bool;
BEGIN
    
   SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
   
   SELECT open,closed,refid INTO p_open,p_closed,p_refid FROM mne_crm.order WHERE orderid = p_orderid;
   IF p_open = false THEN
       RAISE EXCEPTION '#mne_lang#Bestellung ist noch nicht offen#';
   END IF;

   IF p_closed = true THEN
       RAISE EXCEPTION '#mne_lang#Bestellung ist geschlossen#';
   END IF;
   
   FOUND := false;
   SELECT companyid INTO p_refid FROM mne_crm.companyown WHERE companyid = p_refid;
   IF FOUND THEN
       RAISE EXCEPTION '#mne_lang#Kann der eigenen Firma nichts liefern#';
   END IF;
    
    FOUND := false;
    SELECT deliverynoteid, num, contactid INTO p_deliverynoteid, p_num, p_contactid FROM mne_shipment.deliverynote
    WHERE  num = ( SELECT MAX(num) FROM mne_shipment.deliverynote WHERE orderid = p_orderid AND NOT delivered )
    AND    orderid = p_orderid;

    IF FOUND THEN
        RAISE WARNING '#mne_lang#Ergänze letzten Lieferschein#';
        
        p_last = true;
        DELETE FROM mne_shipment.deliverynoteproduct WHERE deliverynoteid = p_deliverynoteid; 
 
        UPDATE mne_shipment.deliverynote
        SET modifyuser = session_user, modifydate = acttime
        WHERE deliverynoteid = p_deliverynoteid;

    ELSE
        p_last = false;
        FOUND := false;
        SELECT DISTINCT max(num) + 1, orderid INTO p_num FROM mne_shipment.deliverynote 
        WHERE orderid = p_orderid GROUP BY orderid;
        IF NOT FOUND THEN
            p_num := 1;
            SELECT ownerid, contactid INTO p_ownerid, p_contactid
            FROM mne_crm.order
            WHERE orderid = p_orderid;
        ELSE
            SELECT ownerid, contactid INTO p_ownerid, p_contactid FROM mne_shipment.deliverynote
            WHERE orderid = p_orderid AND num = p_num - 1;
        END IF;
 
       SELECT max(CAST(deliverynotenumber AS INT4)) + 1 INTO p_deliverynotenumber FROM mne_shipment.deliverynote;
       IF NOT FOUND OR p_deliverynotenumber IS NULL THEN
            p_deliverynotenumber := 1;
        END IF;
        
       SELECT INTO p_deliverynoteid mne_catalog.mk_id();

        /* Tabelle deliverynote füllen */
        /* =========================== */
          createdate, createuser, modifydate, modifyuser )
      SELECT 
              p_deliverynoteid, p_num , p_ownerid, p_contactid, orderid, p_deliverynotenumber,
          acttime , session_user, acttime, session_user
      FROM
          mne_crm.order
      WHERE 
              orderid = p_orderid;
    END IF;


    /* Tabelle deliverynoteproduct füllen */
    /* ================================== */

    FOUND := false;
       createdate, createuser, modifydate, modifyuser )
      SELECT
         p_deliverynoteid, t0.orderproductid, t0.position, ( t0.actcount - COALESCE(sum(t1.count),0)),
     acttime, session_user, acttime, session_user 
      FROM
         mne_crm.orderproduct t0 LEFT JOIN mne_shipment.deliverynoteproduct t1
       ON ( t0.orderproductid = t1.orderproductid) LEFT JOIN mne_crm.order t2 
       ON ( t0.orderid = t2.orderid )
      WHERE t0.orderid = p_orderid AND ( t0.productprice <> 0.0 OR t0.showdeliver = true )
      GROUP BY t0.orderproductid, t0.position, t0.actcount
      HAVING ( t0.actcount - COALESCE(sum(t1.count),0)) <> 0;
 
    GET DIAGNOSTICS i = ROW_COUNT;
    IF i = 0 THEN
        IF p_last THEN
            RAISE WARNING '#mne_lang#Keine Produkte zur Auslieferung fertig#';
        ELSE
            RAISE exception '#mne_lang#Keine Produkte zur Auslieferung fertig#';
        END IF;
    END IF;

   return p_deliverynoteid;
END;
 $$;


