CREATE FUNCTION mne_crm.order_ok(p_orderid character varying, p_ownerid character varying, p_contactid character varying, p_refid character varying, p_ordernumber character varying, p_description character varying, p_text character varying, p_language character varying, p_withvat integer, p_lumpsum integer, p_open boolean, p_closed boolean, p_color character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
  acttime INTEGER;
  result varchar;
  r record;
  r_personid varchar;
  r_companyid varchar;
BEGIN

  SELECT INTO acttime
      CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  r_personid = r_companyid = NULL;
  
  FOUND := FALSE;
  SELECT * INTO r FROM mne_crm.company WHERE companyid = p_refid;
  IF FOUND = true THEN
    r_companyid = p_refid;
  ELSE
    FOUND := FALSE;
    SELECT * INTO r FROM mne_crm.person WHERE personid = p_refid;
    IF FOUND = true THEN
      r_personid = p_refid;
    END IF;
  END IF;
  
  IF r_personid IS NULL AND r_companyid IS NULL THEN
    RAISE EXCEPTION '#mne_lang#Es wurde kein Kunde angegeben#';
  END IF;
  
  IF p_orderid = '################' THEN
    SELECT INTO result mne_catalog.mk_id();
             ordernumber, description , text, language,
             lumpsum ,withvat, open, closed, color,
             companyid, personid,
             createdate, createuser, modifydate, modifyuser )
    VALUES ( result, NULLIF(p_contactid,''), NULLIF(p_ownerid,''), NULLIF(p_refid,''),
             p_ordernumber, p_description, p_text, p_language,
             p_lumpsum, p_withvat, p_open, p_closed, p_color,
             r_companyid, r_personid,
             acttime, session_user, acttime, session_user );
  ELSE

    FOUND := false;
    PERFORM * FROM mne_crm.order WHERE orderid = p_orderid;
    
    IF FOUND <> true then
      RAISE EXCEPTION '#mne_lang# Auftrag wurden nicht gefunden#';
    END IF;
    
    result = p_orderid;
    UPDATE mne_crm.order SET
      ownerid = NULLIF(p_ownerid,''),
      contactid = NULLIF(p_contactid,''),
      refid = NULLIF(p_refid,''),
      ordernumber = p_ordernumber,
      description= p_description,
      text = p_text,
      language = p_language,
      lumpsum = p_lumpsum,
      withvat = p_withvat,
      open = p_open,
      closed = p_closed,
      color = p_color,
      companyid = r_companyid,
      personid = r_personid,
      modifydate = acttime,
      modifyuser = session_user
    WHERE
      orderid = p_orderid;
    
  END IF;
    
  return result;
END; $$;


