CREATE FUNCTION mne_crm.orderproduct_add(p_orderid character varying, p_orderproducttype character varying, p_productid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    p_orderproductid VARCHAR;
    p_position INTEGER;
    r_i INTEGER;
    r RECORD;
    r_productnumber VARCHAR;
 BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    FOUND := FALSE;
    SELECT * INTO r FROM mne_crm.product t0 WHERE t0.productid = p_productid;
    IF NOT FOUND THEN
      RAISE EXCEPTION '#mne_lang#Das Produkt ist unbekannt#';
    END IF;

    r_i := -1;
    SELECT MAX(position) + 1 INTO p_position FROM mne_crm.orderproduct WHERE orderid = p_orderid;
    IF p_position IS NULL THEN
      p_position = 0;
      r_i := 0;
    END IF;
    
    IF r.productnumber = '' OR r.productnumber IS NULL THEN
      LOOP
        PERFORM productnumber FROM mne_crm.orderproduct WHERE orderid = p_orderid AND ( productnumber = CAST( (p_position + r_i ) AS varchar) OR productname = r.name || ' ' || CAST( (p_position + r_i ) AS varchar));
        EXIT WHEN NOT FOUND;
        r_i := r_i + 1;
      END LOOP;
      r_productnumber = CAST( (p_position + r_i ) AS varchar );
    ELSE
      r_productnumber = r.productnumber;
    END IF;
    
    FOUND := FALSE;
    PERFORM orderproductid
    FROM mne_crm.orderproduct t0 LEFT JOIN mne_crm.product t1 ON ( t0.productname = t1.name AND t1.productnumber != '' )
    WHERE t1.productid = p_productid AND t0.orderid = p_orderid;
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Es existiert schon ein Produkt mit gleichem Namen#';
    END IF;
    
    FOUND := FALSE;
    PERFORM orderid
    FROM mne_crm.orderproduct t0 LEFT JOIN mne_crm.product t1 ON ( t0.productnumber = t1.productnumber AND t1.productnumber != '' )
    WHERE t1.productid = p_productid AND t0.orderid = p_orderid;
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Es existiert schon ein Produkt mit gleicher Produktnummer#';
    END IF;

    SELECT INTO p_orderproductid mne_catalog.mk_id();

      actcount,
      count,
      orderid,
      orderproductid,
      orderproducttype,
      withworkingstep,
      position,
      productoptid,
      productid,
      productcurrencyid,
      productcurrencyrate,
      productdescription,
      productnumber,
      productname,
      productprice,
      productpricecalc,
      productcost,
      productcostcalc,
      productcostrecalc,
      productunit,
      productvat,
      showdeliver,
      timeauto,
      createdate, createuser, modifydate, modifyuser )
      SELECT
        0,
        1,
        p_orderid,
        p_orderproductid,
        p_orderproducttype,
        t0.withworkingstep,
        p_position,
        p_productid,
        p_productid,
        COALESCE(t1.currencyid, ( SELECT DISTINCT currencyid FROM mne_crm.companyown tt0 LEFT JOIN mne_base.currency tt1 ON ( tt0.currency = tt1.currency ))),
        t2.rate_purchase,
        t0.description,
        COALESCE(r_productnumber, ''),
        CASE WHEN NULLIF(t0.productnumber,'') IS NULL THEN t0.name || ' ' || r_productnumber ELSE t0.name END,
        COALESCE(t1.unitprice,0),
        COALESCE(t1.unitprice,0),
        COALESCE(t1.unitcost,0),
        COALESCE(t1.unitcost,0),
        true,
        t1.unit,
        t1.vat,
        true,
        true,
        acttime, session_user, acttime, session_user
    FROM 
         ( mne_crm.product t0 LEFT JOIN mne_crm.productprice t1 ON ( t0.productid = t1.productid ))
         LEFT JOIN mne_base.currency t2 ON ( t1.currencyid = t2.currencyid )
        WHERE
          t0.productid = p_productid;

         createdate, createuser, modifydate, modifyuser )
    SELECT
      mne_catalog.mk_id(),
      p_orderproductid,
      COALESCE(t2.duration,0),
      t2.longdesc,
      COALESCE(t2.description,'Allgemeine Arbeiten'),
      COALESCE(t2.skillid,'special'),
      COALESCE(t2.step,0),
      acttime, session_user, acttime, session_user
    FROM mne_crm.product t1
      LEFT JOIN mne_personnal.producttime t2 ON ( t2.productid = t1.productid )
    WHERE t1.productid = p_productid;
    
       createdate, createuser, modifydate, modifyuser )
    SELECT mne_catalog.mk_id(), p_orderproductid, 0, count, partcost, partcost, partgroup, partdescription, partid, unit,
           acttime, session_user, acttime, session_user
    FROM mne_warehouse.productpart
    WHERE productid = p_productid;

    RETURN p_orderproductid;
END

 $$;


