-- Name: orderproduct_calculate(character varying, character varying); Type: FUNCTION; Schema: mne_crm; Owner: admindb
--

CREATE FUNCTION mne_crm.orderproduct_calculate(p_orderid character varying, p_orderproducttype character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
  r RECORD;
  acttime INTEGER;
BEGIN
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    IF p_orderproducttype IS NULL THEN
      FOR r IN SELECT * FROM mne_crm.orderproduct WHERE orderid = p_orderid LOOP
        UPDATE mne_crm.orderproduct
        SET
          productcost = mne_crm.orderproductcost(r.orderproductid),
          modifydate = acttime,
          modifyuser = session_user
        WHERE orderproductid = r.orderproductid;
      END LOOP;
    ELSE
      FOR r IN SELECT * FROM mne_crm.orderproduct WHERE orderid = p_orderid AND orderproducttype = p_orderproducttype LOOP
        UPDATE mne_crm.orderproduct
        SET
          productcost = mne_crm.orderproductcost(r.orderproductid),
          modifydate = acttime,
          modifyuser = session_user
        WHERE orderproductid = r.orderproductid;
      END LOOP;
    END IF;
    
    RETURN 'ok';
END

 $$;


