CREATE FUNCTION mne_crm.orderproduct_ok(p_orderproductid character varying, p_offerproductid character varying, p_position integer, p_withworkingstep boolean, p_productdescription character varying, p_productcurrencyid character varying, p_productid character varying, p_orderproducttype character varying, p_timeauto boolean, p_showdeliver boolean, p_productcostrecalc boolean, p_productpricecalc double precision, p_productcostcalc double precision, p_productvat double precision, p_ready boolean, p_productname character varying, p_productnumber character varying, p_count double precision, p_actcount double precision, p_productunit character varying, p_productprice double precision, p_productcost double precision) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
  acttime int4;
  r RECORD;
  r_offerproductid varchar;
BEGIN
  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  SELECT DISTINCT
    t0."productid" AS productid,
    t2."description" AS description,
    t2."ordernumber" AS ordernumber,
    t2."open" AS open,
    t2."closed" AS closed,
    t0."count" AS count,
    t0."productname" AS productname,
    t0."orderproducttype" AS orderproducttype,
    t0."productnumber" AS productnumber,
    t0."productdescription" AS productdescription,
    t0."productcurrencyid" AS productcurrencyid,
    t0."productprice" AS productprice,
    t0."productunit" AS productunit,
    t0."productvat" AS productvat,
    t0."productpricecalc" AS productpricecalc,
    t1."currency" AS currencycalc,
    t0."productcost" AS productcost,
    t5."currency" AS costcurrency,
    t5."currency" AS costcurrencycalc,
    t0."productcostrecalc" AS productcostrecalc,
    t0."ready" AS ready,
    t1."currency" AS currency,
    CASE WHEN t6.deliverynoteid IS NOT NULL THEN 1 ELSE 0 END AS havedeliverynote
  INTO r
  FROM mne_crm.orderproduct t0 LEFT JOIN mne_base.currency t1 ON ( t0.productcurrencyid = t1.currencyid)
           LEFT JOIN mne_crm.order t2 ON ( t0.orderid = t2.orderid)
           LEFT JOIN mne_crm.person t4 ON ( t2.refid = t4.personid)
           LEFT JOIN mne_crm.companyown t5 ON ( t5.prefix = '' )
           LEFT JOIN mne_shipment.deliverynoteproduct t6 ON ( t0.orderproductid = t6.orderproductid)
  WHERE
    t0.orderproductid = p_orderproductid;
    
  IF r.havedeliverynote = 1 THEN
    IF r.productdescription <> p_productdescription
      OR r.productcurrencyid <> p_productcurrencyid
      OR r.productid <> p_productid
      OR r.orderproducttype <> p_orderproducttype
      OR r.productvat <> p_productvat
      OR r.ready <> p_ready
      OR r.productname <> p_productname
      OR r.productnumber <> p_productnumber
      OR r.count <> p_count
      OR r.productunit <> p_productunit
      OR r.productprice <> p_productprice
    THEN
      RAISE EXCEPTION '#mne_lang#Das Produkt besitzt Lieferscheine und kann nicht mehr geändert werden#';
    END IF;
  END IF;

  r_offerproductid = NULLIF(p_offerproductid,'');
    IF r.productdescription <> p_productdescription
      OR r.productname <> p_productname
      OR r.count <> p_count
      OR r.productprice <> p_productprice
    THEN
      r_offerproductid = NULL;
    END IF;

  UPDATE mne_crm.orderproduct
    SET
      offerproductid = r_offerproductid,
      position = p_position,
      withworkingstep = p_withworkingstep,
      productdescription = p_productdescription,
      productcurrencyid = p_productcurrencyid,
      productid = p_productid,
      orderproducttype = p_orderproducttype,
      timeauto = p_timeauto,
      showdeliver = p_showdeliver,
      productcostrecalc = p_productcostrecalc,
      productpricecalc = p_productpricecalc,
      productcostcalc = p_productcostcalc,
      productvat = NULLIF(p_productvat,0.0),
      ready = p_ready,
      productname = p_productname,
      productnumber = p_productnumber,
      count = p_count,
      actcount = p_actcount,
      productunit = p_productunit,
      productprice = p_productprice,
      productcost = p_productcost,
      
      modifyuser = 'session_user',
      modifydate = acttime
    WHERE orderproductid = p_orderproductid;
    
    PERFORM mne_personnal.orderproducttime_calc(p_orderproductid);
    RETURN p_orderproductid;
END; $$;


