CREATE FUNCTION mne_crm.orderproductcost(p_orderproductid character varying) RETURNS double precision
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    result float8;
    f float8;
BEGIN
    
  SELECT
    SUM(price)
  INTO
    result
  FROM
    (
    SELECT
      orderproductpartid, COALESCE(t1.count * t1.partcost, 0.0) AS price
    FROM 
      mne_crm.orderproduct t0
      left join mne_warehouse.orderproductpart t1 ON ( t0.orderproductid = t1.orderproductid )
    WHERE
      t0.orderproductid = p_orderproductid
    ) tt0;
  
  SELECT
    SUM(price )
  INTO
    f
  FROM 
    (
    SELECT
      orderproducttimeid, COALESCE((t1.setduration / 3600.0) * t2.unitcost, 0.0) as price
    FROM 
      mne_crm.orderproduct t0
      left join mne_personnal.orderproducttime t1 ON ( t0.orderproductid = t1.orderproductid )
      left join mne_personnal.skill t2 ON ( t1.skillid = t2.skillid )
    WHERE
      t0.orderproductid = p_orderproductid
    ) as tt0;
    
  result = result + f;
  
  return result;

END $$;


