CREATE FUNCTION mne_crm.person_add(p_firstname character varying, p_lastname character varying, p_ownerid character varying, p_sex integer, p_sign character varying, p_sorting character varying, p_birthday integer, p_email character varying, p_http character varying, p_role character varying, p_telephonoffice character varying, p_telephonpriv character varying, p_telephonmobil character varying, p_language character varying, p_addressid character varying, p_refid character varying, p_postbox character varying, p_street character varying, p_cityid character varying, p_havepicture character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$ DECLARE 
    acttime INTEGER;
    r_addressid VARCHAR;
    r_personid VARCHAR;
    pic VARCHAR[];
    
BEGIN
  SELECT INTO acttime
    CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
  
  SELECT mne_catalog.mk_id() INTO r_personid;
  VALUES ( r_personid, p_firstname, p_lastname, NULLIF(p_ownerid,''), p_sex, p_sign, NULLIF(p_sorting,''), NULLIF(p_refid,''), session_user, acttime, session_user, acttime );

  VALUES ( r_personid, p_birthday, p_email, p_http, p_language, p_role, p_telephonmobil, p_telephonpriv, p_telephonoffice, session_user, acttime, session_user, acttime);

  IF p_addressid = '' AND p_refid != '' THEN
    DELETE FROM mne_crm.address WHERE refid = r_personid AND addresstypid = '000000000001';
  ELSE
    SELECT addressid INTO r_addressid FROM mne_crm.address WHERE refid = r_personid AND addresstypid = '000000000001';
    IF FOUND THEN
      UPDATE mne_crm.address SET
        cityid = p_cityid,
        postbox = p_postbox,
        street = p_street,
        refid = p_personid,
        
        modifyuser = session_user,
        modifydate = acttime
      WHERE
        addressid = r_addressid;
    ELSE
      SELECT mne_catalog.mk_id() INTO r_addressid;
      VALUES ( r_addressid, '000000000001', NULLIF(p_cityid,''), p_postbox, p_street, r_personid, session_user, acttime, session_user, acttime);
    END IF;
  END IF;
  
  IF p_havepicture != '' THEN
    PERFORM personpictureid FROM mne_crm.personpicture WHERE personpictureid = r_personid;
    pic = regexp_split_to_array(p_havepicture, ':');
    IF FOUND THEN
      UPDATE mne_crm.personpicture SET
        picturetype = pic[1],
        picture = pic[2],
        
        modifyuser = session_user,
        modifydate = acttime
      WHERE
        personpicture = p_personid;
    ELSE
      VALUES ( r_personid, pic[1], pic[2], session_user, acttime, session_user, acttime );
    END IF;
  END IF;
    
  return r_personid;
END $$;


