CREATE FUNCTION mne_crm.person_vcard(p_personid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
r RECORD;
r_uuid VARCHAR;
retval varchar;
BEGIN
  
  SELECT INTO r_uuid uuid FROM mne_catalog.uuid;
  
  SELECT DISTINCT INTO r t0."personid" AS personid,
                  t0."firstname" AS firstname,
                 t0."lastname" AS lastname,
                 (t0.firstname || E' ' || t0.lastname ) AS fullname,
                 COALESCE(( CASE WHEN t6.addressid isnull THEN t3.street ELSE t6.street END),'') AS street,
                 COALESCE(( CASE WHEN t6.addressid isnull THEN t3.postbox ELSE t6.postbox END),'') AS postbox,
                 COALESCE(( CASE WHEN t6.addressid isnull THEN t4.name ELSE t7.name END),'') AS city,
                 COALESCE(( CASE WHEN t6.addressid isnull THEN t4.postcode ELSE t7.postcode END),'') AS postcode,
                 COALESCE(( CASE WHEN t6.addressid isnull THEN t5.name_de ELSE t8.name_de END),'') AS country,
                 COALESCE(( CASE WHEN t6.addressid isnull THEN t5.phoneprefix ELSE t8.phoneprefix END),'') AS phoneprefix,
                 COALESCE(mne_catalog.epoch_format(t1.birthday,'YYYY-MM-DD' ),'') AS birthday,
                 COALESCE(NULLIF(t1."http",''), t15.http,'') AS http,
                 COALESCE(t1."telephonoffice",'') AS telephonoffice,
                 COALESCE(t1."email",'') AS email,
                 COALESCE(t1."telephonmobil",'') AS telephonmobil,
                 COALESCE(t1."telephonpriv",'') AS telephonpriv,
                 COALESCE(t9.picture,'') AS picture,
                 COALESCE(( CASE WHEN t9.picturetype = 'images/png' THEN 'PNG' WHEN t9.picturetype = 'image/jpeg' THEN 'JPG' ELSE t9.picturetype END ),'') as picturetype,
                 COALESCE(t2.name, E'') AS refname,
                 COALESCE(t6."addressid", t3."addressid", '')  AS addressid,
                 COALESCE(CASE WHEN t6.addressid isnull THEN t4.cityid ELSE t7.cityid END,'') AS cityid,
                 COALESCE(CASE WHEN t6.addressid isnull THEN t5.countryid ELSE t8.countryid END,'') AS countryid ,
                 mne_catalog.epoch_format(t0.modifydate,'YYYY-MM-DD' ) || 'T' || mne_catalog.epoch_format(t0.modifydate,'HH24MI+SS:00' ) as modifydate
  FROM
    mne_crm.person t0 LEFT JOIN mne_crm.persondata t1 ON ( t0.personid = t1.persondataid)
    LEFT JOIN mne_crm.company t2 ON ( t0.refid = t2.companyid) 
    LEFT JOIN mne_crm.companydata t15 ON ( t2.companyid = t15.companydataid ) 
    LEFT JOIN mne_crm.address t3 ON ( t2.companyid = t3.refid AND t3.addresstypid = '000000000001' ) 
    LEFT JOIN mne_crm.city t4 ON ( t3.cityid = t4.cityid) 
    LEFT JOIN mne_crm.country t5 ON ( t4.countryid = t5.countryid)
    LEFT JOIN mne_crm.address t6 ON ( t0.personid = t6.refid AND t6.addresstypid = '000000000001' )
    LEFT JOIN mne_crm.city t7 ON ( t6.cityid = t7.cityid) 
    LEFT JOIN mne_crm.country t8 ON ( t7.countryid = t8.countryid) 
    LEFT JOIN mne_crm.personpicture t9 ON ( t0.personid = t9.personpictureid)
  WHERE
    ( t0.personid = p_personid );

   retval = E'BEGIN:VCARD\r\n'
      ||  E'VERSION:3.0\r\n'
      ||  E'N:' || r.lastname || ';' || r.firstname || E';;;\r\n'
      ||  E'UID:' || r.personid || E'@erpserver_p_' || r_uuid || E'\r\n'
      ||  E'URI:' || r.personid || E'@erpserver_p_' || r_uuid || E'\r\n'
      ||  E'REV:' || r.modifydate || E'\r\n'
      ||  E'FN:' || r.fullname || E'\r\n'
      ||  E'PRODID:-//Open Source Erp//Persons//EN\r\n'
      ||  E'CATEGORIES:ERP\r\n'
      ||  E'ORG:' || r.refname || E'\r\n'
      ||  E'EMAIL;TYPE=WORK:' || r.email || E'\r\n'
      ||  E'TEL;TYPE=HOME:' || r.telephonpriv || E'\r\n'
      ||  E'TEL;TYPE=WORK:' || r.telephonoffice || E'\r\n'
      ||  E'TEL;TYPE=CELL:' || r.telephonmobil || E'\r\n'
      ||  E'URL;TYPE=WORK:' || r.http || E'\r\n'
      ||  E'ADR;TYPE=WORK:;;' || r.street || ';' || r.city || ';;' || r.postcode || ';' || r.country || E'\r\n'
      ||  E'NOTE:\r\n';
    IF r.birthday != '' THEN
      retval = retval || E'BDAY;VALUE=DATE:' || r.birthday || E'\r\n';
    END IF;
    IF r.picture != '' AND r.picturetype != '' THEN
      retval = retval || E'PHOTO;ENCODING=b;TYPE='|| r.picturetype || ':' || regexp_replace(r.picture, '[\r\n]', '', 'g') || E'\r\n';
    END IF;

    retval = retval ||   E'END:VCARD\r\n';
    
    return retval;
END $$;


