    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
  acttime INTEGER;
  result TEXT;
  r RECORD;
  str VARCHAR;
BEGIN
    
  SELECT INTO acttime
    CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  SELECT mne_catalog.mk_id() INTO result;

  SELECT t2.companyid IS NOT NULL AS owncompany INTO r
    FROM mne_crm.person t1
      LEFT JOIN mne_crm.companyown t2 ON ( t1.refid = t2.companyid )
    WHERE t1.personid = p_personid;
  
      createdate, modifydate, createuser, modifyuser )
  VALUES
    ( result, p_personid, NULLIF(p_loginname, ''), p_color, p_active AND r.owncompany,
      acttime, acttime, session_user, session_user );

  IF p_canlogin THEN
    PERFORM mne_catalog.useradd(p_loginname, p_validuntil );
  END IF;
  
  return result;
