CREATE FUNCTION mne_crm.personowndata_mod(p_personowndataid character varying, p_personid character varying, p_loginname character varying, p_active boolean, p_canlogin boolean, p_validuntil integer, p_color character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
  acttime INTEGER;
  r RECORD;
  str VARCHAR;
BEGIN
    
  SELECT INTO acttime
    CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);


  SELECT t0.* , t2.companyid IS NOT NULL AS owncompany INTO r
    FROM mne_crm.personowndata t0
      LEFT JOIN mne_crm.person t1 ON ( t0.personid = t1.personid )
      LEFT JOIN mne_crm.companyown t2 ON ( t1.refid = t2.companyid )
    WHERE t0.personowndataid = p_personowndataid;

  UPDATE mne_crm.personowndata
    SET
      personid = p_personid,
      loginname = NULLIF(p_loginname,''),
      color = p_color,
      active = p_active AND r.owncompany,

      modifydate = acttime,
      modifyuser = session_user
    WHERE
      personowndataid = p_personowndataid;
  
  IF COALESCE(p_loginname,'') != '' AND ( p_canlogin ) THEN
    IF p_loginname != r.loginname AND r.loginname != '' THEN
      PERFORM mne_catalog.userdel(r.loginname);
    END IF;
    PERFORM mne_catalog.useradd( p_loginname, p_validuntil);
  ELSEIF r.loginname != '' THEN
    PERFORM mne_catalog.userdel( r.loginname);
  END IF;

  return 'ok';
END $$;


