CREATE FUNCTION mne_crm.personsharepasswd_ok(p_personid character varying, p_loginname character varying, p_passwd character varying, p_validuntil integer) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
  acttime INTEGER;
  r_passwd VARCHAR;
BEGIN
  SELECT INTO acttime
      CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  PERFORM loginname FROM mne_personnal.personowndata WHERE personid = p_personid;
  IF FOUND THEN
    RAISE EXCEPTION '%', '#mne_lang#Benutzer hat ERP Zugriff - bitte im Personalmodul verwalten';
  END IF;
    
  IF p_loginname = '' OR p_passwd = '' THEN
     DELETE FROM mne_crm.personsharepasswd WHERE personid = p_personid;
     return p_personid;
  END IF;
  
  r_passwd = p_passwd;
  IF p_passwd != '' THEN
    r_passwd = '{SHA}'||encode(digest(p_passwd,'sha1'),'base64');
  END IF;


  PERFORM personid FROM mne_crm.personsharepasswd WHERE personid = p_personid;
  IF FOUND THEN

    IF p_passwd != '********' THEN
      UPDATE mne_crm.personsharepasswd SET password = NULLIF(r_passwd,'') WHERE personid = p_personid;
    END IF;
    
    UPDATE mne_crm.personsharepasswd SET
      loginname  = p_loginname,
      validuntil = NULLIF(p_validuntil, 0),
      modifydate = acttime,
      modifyuser = session_user
    WHERE
      personid = p_personid;

  ELSE

    IF p_passwd = '********' THEN
      RAISE EXCEPTION '%', '#mne_lang#Bitte Passwort angeben#';
    END IF;

    PERFORM loginname FROM mne_personnal.personowndata WHERE loginname = p_loginname AND personid != p_personid;
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Der Loginname ist schon vergeben#';
    END IF;
    

  END IF;
  
  return p_personid;
END; $$;


