CREATE FUNCTION mne_crm.productcost(p_productid character varying) RETURNS double precision
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    result float8;
    f float8;
BEGIN
    
  SELECT
    SUM(price)
  INTO
    result
  FROM
    (
    SELECT
     productpartid, COALESCE(t1.count * t1.partcost, 0.0) AS price
    FROM 
      mne_crm.product t0
      left join mne_warehouse.productpart t1 ON ( t0.productid = t1.productid )
    WHERE
      t0.productid = p_productid
    ) tt0;
  
  SELECT
    SUM(price )
  INTO
    f
  FROM 
    (
    SELECT
      producttimeid, COALESCE((t1.duration / 3600.0) * t2.unitcost, 0.0) as price
    FROM 
      mne_crm.product t0
      left join mne_personnal.producttime t1 ON ( t0.productid = t1.productid )
      left  join mne_personnal.skill t2 ON ( t1.skillid = t2.skillid )
    WHERE
      t0.productid = p_productid
    ) as tt0;
    
  result = result + f;
  
  return result;

END $$;


