    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    r RECORD;
 BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

   UPDATE mne_crm.offerproduct 
   SET productcost = p_cost, modifyuser = session_user, modifydate = acttime
   WHERE offerproductid IN ( SELECT DISTINCT t0.offerproductid FROM mne_crm.offerproduct t0
                                 LEFT JOIN mne_personnal.offerproducttime t1 ON t0.offerproductid = t1.offerproductid
