CREATE FUNCTION mne_crm.productprice_ok(p_productid character varying, p_unitcost double precision, p_unitprice double precision, p_unit character varying, p_vat double precision, p_currencyid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    result varchar;
    acttime integer;
    r_cost float8;
    
BEGIN
    
   SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

   r_cost = 0.0;
   SELECT COALESCE(mne_crm.productcost(p_productid), 0) INTO r_cost;
   IF r_cost = 0.0 THEN
       r_cost = p_unitcost;
   END IF;
   
   IF r_cost <> p_unitcost THEN
       RAISE WARNING '#mne_lang#Kosten werden mit hinterlegten Kosten überschrieben#';
   END IF;
   
   FOUND = FALSE;
   SELECT p_productid INTO result FROM mne_crm.productprice WHERE productid = p_productid;
   IF FOUND = FALSE THEN

     result = p_productid;

         createuser, createdate, modifyuser, modifydate )
     VALUES
       ( result, r_cost, p_unitprice, p_unit, NULLIF(p_vat, 0.0), NULLIF(p_currencyid,''),
         session_user, acttime, session_user, acttime );

   ELSE

     result = p_productid;
     
        UPDATE mne_crm.productprice SET
       unitcost = r_cost,
       unitprice = p_unitprice,
       unit = p_unit, 
       vat = NULLIF(p_vat, 0.0),
       currencyid =  NULLIF(p_currencyid,''),
       modifyuser = session_user,
       modifydate = acttime
     WHERE
       productid = p_productid;

   END IF;
   
   PERFORM mne_crm.productcost_update(p_productid, r_cost);
   PERFORM mne_crm.productprice_update(p_productid, p_unitprice);
   return result;
END $$;


