--
-- Name: producttree_leaf(character varying); Type: FUNCTION; Schema: mne_crm; Owner: admindb
--

CREATE FUNCTION mne_crm.producttree_leaf(p_parentid character varying) RETURNS SETOF mne_crm.producttree
    LANGUAGE plpgsql
    AS $$ 
DECLARE
    r mne_crm.producttree%rowtype;
    rr mne_crm.producttree%rowtype;
    s varchar;
    anzahl integer;
    found boolean;
    oproductid varchar;
BEGIN
    IF p_parentid is null THEN
      s = 'SELECT * FROM mne_crm.producttree WHERE parentid is null';
    ELSE
      s = E'SELECT * FROM mne_crm.producttree WHERE parentid = \'' || p_parentid || E'\'';
    END IF;
    
    found = false;
    FOR r in EXECUTE s
    LOOP
        found = true;
        SELECT COUNT(*) INTO anzahl FROM mne_crm.producttree WHERE parentid = r.treeid;
        IF anzahl > 0 THEN
            oproductid = '';
            FOR rr in select * from product_tree(r.treeid)
            LOOP
                RETURN NEXT rr;
                IF rr.productid <> oproductid THEN
                    rr.parentid = p_parentid;
                    oproductid = rr.productid;
                    RETURN NEXT rr;
                END IF;
            END LOOP;
        ELSEIF NOT r.productid IS NULL THEN
            r.parentid = r.treeid;
            RETURN NEXT r;
        END IF;
    END LOOP;

    IF found = false THEN
        SELECT * INTO r FROM mne_crm.producttree WHERE treeid = p_parentid;
        r.parentid = p_parentid;
        RETURN NEXT r;
    END IF;
    
    RETURN;
END
 $$;


