-- Name: vcard_parse(character varying); Type: FUNCTION; Schema: mne_crm; Owner: admindb
--

CREATE FUNCTION mne_crm.vcard_parse(p_vcard character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
  acttime INT4;
  r_personid varchar;
  r_companyid VARCHAR;
  r_cid VARCHAR;
  
  r RECORD;
  r_company RECORD;
  r_cdata RECORD;
  r_person RECORD;
  r_pdata RECORD;
  r_address RECORD;
  r_city RECORD;
  r_country RECORD;
  
  r_vcard VARCHAR;
  r_str VARCHAR;
  r_typ VARCHAR;
  r_id  TEXT[];
  r_vals TEXT[];
  r_addr TEXT[];
  
  r_nophoto BOOLEAN;
  r_i INTEGER;
BEGIN

  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
  
  r_nophoto = TRUE;
  r_vcard = p_vcard;
  r_personid = '';
  r_companyid = '';
  
  /* RAISE WARNING '%', r_vcard; */
  
  FOR r IN SELECT regexp_split_to_table(r_vcard, E'\n') AS str LOOP
      IF substr(r.str, 1, strpos(r.str, ':')) = 'UID:' THEN
        r_str = substr(r.str,strpos(r.str, ':' ) + 1);

        IF position('erpserver_p' in r_str) > 1 THEN
          r_personid = substr(r_str, 1, strpos(r_str, '@') -1 );
        ELSIF position('erpserver_c' in r_str) > 1 THEN
          r_companyid = substr(r_str, 1, strpos(r_str, '@') -1 );
        ELSE
          RAISE WARNING 'no erp contact %', E'\r\n' || r_vcard;
          return '';
        END IF;
         
        IF r_personid <> '' THEN
         
          SELECT INTO r DISTINCT t0."personid" AS personid,
            COALESCE(t6."addressid", t3."addressid")  AS addressid,
            CASE WHEN t6.addressid isnull THEN t4.cityid ELSE t7.cityid END AS cityid,
            CASE WHEN t6.addressid isnull THEN t5.countryid ELSE t8.countryid END AS countryid
          FROM
            mne_crm.person t0 LEFT JOIN mne_crm.persondata t1 ON ( t0.personid = t1.persondataid)
            LEFT JOIN mne_crm.company t2 ON ( t0.refid = t2.companyid) 
            LEFT JOIN mne_crm.companydata t15 ON ( t2.companyid = t15.companydataid ) 
            LEFT JOIN mne_crm.address t3 ON ( t2.companyid = t3.refid AND t3.addresstypid = '000000000001' ) 
            LEFT JOIN mne_crm.city t4 ON ( t3.cityid = t4.cityid) 
            LEFT JOIN mne_crm.country t5 ON ( t4.countryid = t5.countryid)
            LEFT JOIN mne_crm.address t6 ON ( t0.personid = t6.refid AND t6.addresstypid = '000000000001' )
            LEFT JOIN mne_crm.city t7 ON ( t6.cityid = t7.cityid) 
            LEFT JOIN mne_crm.country t8 ON ( t7.countryid = t8.countryid)
          WHERE
            t0.personid = r_personid;
           
        ELSE
          SELECT INTO r DISTINCT t0."companyid" AS companyid,
            t3."addressid" AS addressid,
            t4.cityid AS cityid,
            t5.countryid AS countryid
          FROM
             mne_crm.company t0 LEFT JOIN mne_crm.companydata t1 ON ( t0.companyid = t1.companydataid)
             LEFT JOIN mne_crm.address t3 ON ( t0.companyid = t3.refid AND t3.addresstypid = '000000000001' ) 
             LEFT JOIN mne_crm.city t4 ON ( t3.cityid = t4.cityid) 
             LEFT JOIN mne_crm.country t5 ON ( t4.countryid = t5.countryid)
          WHERE
            t0.companyid = r_companyid;
            
        END IF;
         
        SELECT * INTO r_person from mne_crm.person     WHERE personid     = r_personid;
        SELECT * INTO r_pdata  from mne_crm.persondata WHERE persondataid = r_personid;

        SELECT * INTO r_company from mne_crm.company     WHERE companyid     = r_companyid;
        SELECT * INTO r_cdata   from mne_crm.companydata WHERE companydataid = r_companyid;

        SELECT * INTO r_address from mne_crm.address   WHERE addressid    = r.addressid;
        SELECT * INTO r_city    from mne_crm.city      WHERE cityid       = r.cityid;
        SELECT * INTO r_country from mne_crm.country   WHERE countryid    = r.countryid;
        
        EXIT;
         
      END IF;

  END LOOP;

  IF r_personid = '' AND r_companyid = '' THEN
    RAISE WARNING 'no erp contact %', E'\r\n' || r_vcard;
    return '';
  END IF;

  FOR r IN SELECT regexp_split_to_table(r_vcard, E'\n') AS str LOOP

      IF strpos(r.str,':' ) = 0 THEN
         CONTINUE;
      END IF;
      
      r_str = substr(r.str, 1, strpos(r.str, ':') - 1);
      r_id = regexp_split_to_array(r_str, ';');
      r_str = substr(r.str,strpos(r.str, ':' ) + 1);
      r_vals = regexp_split_to_array(r_str, ';');
      
      r_id[1] = regexp_replace(r_id[1], '^ITEM.*\.', '');

      IF r_id[1] = 'N' THEN
        IF r_vals[1] != '' THEN r_person.lastname = r_vals[1];  ELSE r_person.lastname = 'name';  END IF;
        IF r_vals[2] != '' THEN r_person.firstname = r_vals[2]; ELSE r_person.firstname = 'no'; END IF;
        IF r_vals[3] != '' THEN r_person.firstname = COALESCE(NULLIF(r_vals[2],'') || ' ', '') || r_vals[3]; END IF;
      END IF;

      IF r_id[1] = 'ORG' THEN
        r_company.name = r_vals[1];
        IF r_personid <> '' THEN
          SELECT companyid INTO r_companyid FROM mne_crm.company WHERE name = r_vals[1];
          IF FOUND THEN 
            r_person.refid = r_companyid;
            r_companyid = '';
          ELSE
            SELECT mne_catalog.mk_id() INTO r_cid;
              VALUES ( r_cid, r_vals[1], r_person.ownerid, acttime, r_person.createuser, acttime, r_person.createuser );
              VALUES ( r_cid, '', '', '', '', 'de', '', '', acttime, r_person.createuser, acttime, r_person.createuser );
            r_person.refid = r_cid;
          END IF;
        END IF;
      END IF;
      
      IF r_id[1] = 'EMAIL' THEN
        r_pdata.email = r_vals[1];
        r_cdata.email = r_vals[1];
        FOREACH r_typ IN ARRAY r_id LOOP
          IF r_typ = 'TYPE=WORK' THEN
            r_pdata.email = r_vals[1];
            r_cdata.email = r_vals[1];
          END IF;
        END LOOP;
      END IF;

      IF r_id[1] = 'PHOTO' THEN
        r_nophoto = FALSE;
        FOREACH r_typ IN ARRAY r_id LOOP
          IF r_typ = 'TYPE=PNG' OR r_typ = 'TYPE=image/png' THEN
            DELETE FROM mne_crm.personpicture WHERE personpictureid = r_personid;
          END IF;
          IF r_typ = 'TYPE=JPG' OR r_typ = 'TYPE=JPEG' OR r_typ = 'TYPE=image/jpeg' OR r_typ = 'TYPE=image/jpg' THEN
            DELETE FROM mne_crm.personpicture WHERE personpictureid = r_personid;
          END IF;
        END LOOP;
      END IF;
      
      IF r_id[1] = 'TEL' THEN
        SELECT INTO r_str regexp_replace(r_vals[1], '[^0-9]+', '', 'g');
        FOREACH r_typ IN ARRAY r_id LOOP
          r_typ = upper(r_typ);
          IF r_typ = 'TYPE=WORK' OR r_typ = 'TYPE=WORK,VOICE' THEN
            r_pdata.telephonoffice = r_str;
            r_cdata.telefon = r_str;
          END IF;
          IF r_typ = 'TYPE=HOME' OR r_typ = 'TYPE=HOME,VOICE' THEN
            r_pdata.telephonpriv = r_str;
            IF r_cdata.telefon = '' OR r_cdata.telefon IS NULL THEN
              r_cdata.telefon = r_str;
            END IF;
          END IF;
          IF r_typ = 'TYPE=MOBIL' OR r_typ = 'TYPE=MOBIL,VOICE' THEN
            r_pdata.telephonmobil = r_str;
            r_cdata.mobile = r_str;
          END IF;
          IF r_typ = 'TYPE=IPHONE' OR r_typ = 'TYPE=IPHONE,VOICE' THEN
            r_pdata.telephonmobil = r_str;
          END IF;
        END LOOP;
      END IF;
      
      IF r_id[1] = 'URL' THEN
        FOREACH r_typ IN ARRAY r_id LOOP
          r_typ = upper(r_typ);
          IF r_typ = 'TYPE=WORK' AND r_vals[1] != r_pdata.http AND r_pdata.http != '' THEN
            r_pdata.http = r_vals[1];
            r_cdata.http = r_vals[1];
          END IF;
        END LOOP;
      END IF;

      IF r_id[1] = 'ADR' THEN
        IF r_typ IS NULL THEN
          r_addr = r_vals;
        END IF;
        FOREACH r_typ IN ARRAY r_id LOOP
          r_typ = upper(r_typ);
          IF r_typ = 'TYPE=WORK' THEN
            r_addr = r_vals;
          END IF;
          IF r_typ = 'TYPE=HOME' AND r_addr IS NULL THEN
            r_addr = r_vals;
          END IF;
        END LOOP;
      END IF;
       
  END LOOP;

  IF r_addr IS NOT NULL THEN
    IF ( r_city.cityid IS NULL OR r_city.name != r_addr[4]  OR r_city.postcode != r_addr[6] ) THEN
      SELECT * INTO r_city FROM mne_crm.city WHERE name = r_addr[4] AND postcode = r_addr[6];
      IF NOT FOUND THEN
        SELECT mne_catalog.mk_id() INTO r_str;
        SELECT * INTO r_city FROM mne_crm.city WHERE cityid = r_str;
      END IF;
      r_address.cityid = r_city.cityid;
    END IF;
            
    IF r_address.addressid IS NULL THEN
      SELECT mne_catalog.mk_id() INTO r_str;
      SELECT * INTO r_address FROM mne_crm.address WHERE addressid = r_str;
    END IF;

    r_address.postbox = r_addr[1];
    r_address.street = regexp_replace(regexp_replace(r_addr[3], '\\n', E'\n', 'g'), E'\n+', E' ', 'g');
  END IF;
  
  IF r_nophoto THEN
    DELETE FROM mne_crm.personpicture WHERE personpictureid = r_personid;
  END IF;
  
  UPDATE mne_crm.company SET (name, modifydate, modifyuser ) = (r_company.name, acttime, session_user ) WHERE companyid = r_companyid;
  UPDATE mne_crm.companydata SET (telefon, http, email, modifydate, modifyuser ) = (r_cdata.telefon, r_cdata.http, r_cdata.email, acttime, session_user ) WHERE companydataid = r_companyid;
  
  UPDATE mne_crm.person SET (firstname, lastname, refid, modifydate, modifyuser ) = (r_person.firstname, r_person.lastname, r_person.refid, acttime, session_user ) WHERE personid = r_personid;
  UPDATE mne_crm.persondata SET ( email, http, telephonmobil, telephonoffice, telephonpriv, modifydate, modifyuser ) = ( r_pdata.email, r_pdata.http, r_pdata.telephonmobil, r_pdata.telephonoffice, r_pdata.telephonpriv, acttime, session_user ) WHERE persondataid = r_personid;

  UPDATE mne_crm.address SET ( postbox, street, cityid, modifydate, modifyuser ) = ( r_address.postbox, r_address.street, r_address.cityid, acttime, session_user ) WHERE addressid = r_address.addressid;
  DELETE FROM mne_crm.city WHERE cityid NOT IN ( SELECT DISTINCT cityid FROM mne_crm.address ) AND ( name = '' OR postcode = '' ); 

  return 'ok';
END $$;


