--
-- Name: fixturetypecost_del(character varying); Type: FUNCTION; Schema: mne_fixture; Owner: admindb
--

CREATE FUNCTION mne_fixture.fixturetypecost_del(p_fixturetypecostid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    r_fixturetypeid varchar;
    r_count integer;
 BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    SELECT fixturetypeid INTO r_fixturetypeid FROM mne_fixture.fixturetypecost WHERE fixturetypecostid = p_fixturetypecostid;
    SELECT COUNT(*) INTO r_count FROM mne_warehouse.productpart WHERE fixturetypeid = r_fixturetypeid;
    IF r_count <> 0 THEN
      RAISE EXCEPTION '#mne_lang#Die Kosten werden noch von einem Produkt benötigt#';
    END IF;
    
    SELECT COUNT(*) INTO r_count FROM mne_warehouse.offerproductpart WHERE fixturetypeid = r_fixturetypeid;
    IF r_count <> 0 THEN
      RAISE EXCEPTION '#mne_lang#Die Kosten werden noch von einem Angebot benötigt#';
    END IF;

    SELECT COUNT(*) INTO r_count FROM mne_warehouse.orderproductpart WHERE fixturetypeid = r_fixturetypeid;
    IF r_count <> 0 THEN
      RAISE EXCEPTION '#mne_lang#Die Kosten werden noch von einem Auftrag benötigt#';
    END IF;

    DELETE FROM mne_fixture.fixturetypecost WHERE fixturetypecostid = p_fixturetypecostid;
    
    RETURN 'ok';
END

 $$;


