--
-- Name: fixturetypecost_ok(character varying, character varying, double precision, character varying); Type: FUNCTION; Schema: mne_fixture; Owner: admindb
--

CREATE FUNCTION mne_fixture.fixturetypecost_ok(p_fixturetypecostid character varying, p_fixturetypeid character varying, p_unitcost double precision, p_unit character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    result varchar;
    r RECORD;
 BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    IF p_fixturetypecostid = '################' THEN
        SELECT INTO result mne_catalog.mk_id();
            createuser, createdate, modifyuser, modifydate )
        VALUES
          ( result, p_fixturetypeid, p_unitcost, p_unit,
            session_user, acttime, session_user, acttime );
    ELSE
        result = p_fixturetypecostid;
        UPDATE mne_fixture.fixturetypecost
        SET
          fixturetypeid = p_fixturetypeid,
          unitcost = p_unitcost,
          unit = p_unit,
          modifyuser = session_user,
          modifydate = acttime
        WHERE
          fixturetypecostid = result;
    END IF;

    UPDATE mne_warehouse.productpart SET partcost = p_unitcost WHERE fixturetypeid = p_fixturetypeid;
    UPDATE mne_warehouse.offerproductpart SET partcost = p_unitcost WHERE fixturetypeid = p_fixturetypeid;
    UPDATE mne_warehouse.orderproductpart SET partcost = p_unitcost WHERE fixturetypeid = p_fixturetypeid;

    FOR r IN SELECT DISTINCT productid from mne_warehouse.productpart WHERE fixturetypeid = p_fixturetypeid LOOP
      UPDATE mne_crm.productprice SET unitcost = mne_crm.productcost(r.productid), modifyuser = session_user, modifydate = acttime WHERE productid = r.productid;
      PERFORM mne_crm.productcost_update(r.productid, mne_crm.productcost(r.productid));
    END LOOP;

    FOR r IN SELECT DISTINCT offerproductid from mne_warehouse.offerproductpart WHERE fixturetypeid = p_fixturetypeid LOOP
      UPDATE mne_crm.offerproduct SET productcost = mne_crm.offerproduct_cost(r.offerproductid)
      WHERE offerproductid = r.offerproductid AND productcostrecalc = true;
    END LOOP;
    
    FOR r IN SELECT DISTINCT orderproductid from mne_warehouse.orderproductpart WHERE fixturetypeid = p_fixturetypeid LOOP
      UPDATE mne_crm.orderproduct SET productcost = mne_crm.orderproductcost(r.orderproductid)
      WHERE orderproductid = r.orderproductid AND productcostrecalc = true;
    END LOOP;

    RETURN result;
END

 $$;


