--

CREATE FUNCTION mne_personnal.offerproducttime_calc(p_offerproductid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    r RECORD;
 BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    SELECT * INTO r FROM mne_crm.offerproduct WHERE offerproductid = p_offerproductid;
    IF NOT FOUND THEN
        RAISE EXCEPTION '#mne_lang#Das Produkt existiert nicht#';
    END IF;
    
    IF r.productunit = '/h' THEN
       SELECT SUM(setduration) as setduration, MIN(setduration) as mduration, COUNT(setduration) as "count"  INTO r FROM mne_personnal.offerproducttime WHERE offerproductid = p_offerproductid;
       IF r.setduration <> 3600 OR r.mduration = 0 THEN
           UPDATE mne_personnal.offerproducttime 
           SET 
             setduration = CAST (3600.0 / CAST( r.count AS FLOAT ) AS INT),
             modifydate = acttime,
             modifyuser = session_user
           WHERE offerproductid = p_offerproductid;
       END IF;
    END IF;
    
    RETURN p_offerproductid;
END

 $$;


