    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    acttime integer;
    r_offerproductid varchar;
    r_count int;
BEGIN
    
   SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

   SELECT offerproductid INTO r_offerproductid FROM mne_personnal.offerproducttime WHERE offerproducttimeid = p_offerproducttimeid;
   SELECT COUNT(*) INTO r_count FROM mne_personnal.offerproducttime WHERE offerproductid = r_offerproductid;
   
   IF r_count = 1 THEN
      PERFORM productid FROM mne_crm.offerproduct WHERE offerproductid = r_offerproductid AND productid IS NULL;
      IF FOUND THEN
        RAISE WARNING '#mne_lang#Der letzte Arbeitsschritt wurde gelöscht#';
      END IF;
   END IF;
   
   DELETE FROM mne_personnal.offerproducttime WHERE offerproducttimeid = p_offerproducttimeid;
    
   PERFORM mne_personnal.offerproducttime_calc(r_offerproductid); 
   
   UPDATE mne_crm.offerproduct
   SET productcost = mne_crm.offerproduct_cost(offerproductid), modifydate = acttime, modifyuser = session_user
   WHERE offerproductid = r_offerproductid AND productcostrecalc = true;
   
   return 'ok';
END
 $$;


