CREATE FUNCTION mne_personnal.product_timecalc(p_productid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    r RECORD;
 BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    SELECT * INTO r FROM mne_crm.productprice WHERE productid = p_productid;
    IF NOT FOUND THEN
        RAISE EXCEPTION '#mne_lang#Es existiert kein Preis für das Produkt - bitte erst einen Preis anlegen#';
    END IF;
    
    IF r.unit = '/h' THEN
       SELECT SUM(duration) as duration, MIN(duration) as mduration, COUNT(duration) as "count"  INTO r FROM mne_personnal.producttime WHERE productid = p_productid;
       IF r.duration <> 3600 OR r.mduration = 0 THEN
           UPDATE mne_personnal.producttime 
           SET 
             duration = CAST (3600.0 / CAST( r.count AS FLOAT ) AS INT),
             modifydate = acttime,
             modifyuser = session_user
           WHERE productid = p_productid;
       END IF;
    END IF;
    
    RETURN p_productid;
END

 $$;


