CREATE FUNCTION mne_personnal.skill_update(p_skillid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    acttime integer;
    r_cost float8;
    r record;
BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  FOR r in SELECT * FROM mne_personnal.producttime WHERE skillid = p_skillid 
  LOOP
   r_cost = 0.0;
   SELECT mne_crm.productcost(r.productid) INTO r_cost;
   UPDATE mne_crm.productprice 
   SET 
     unitcost = r_cost,
     modifydate = acttime,
     modifyuser = session_user
   WHERE productid = r.productid;
    
   PERFORM mne_crm.productcost_update(r.productid, r_cost);
  END LOOP;
  
  FOR r in SELECT orderid from mne_personnal.orderproducttime t0 LEFT JOIN mne_crm.orderproduct t1 ON ( t0.orderproductid = t1.orderproductid ) WHERE skillid = p_skillid
  LOOP
    PERFORM mne_crm.orderproduct_calculate(r.orderid, null);
  END LOOP;
  
   return 'ok';
END $$;


