-- Name: slotend(integer, integer, integer, integer, integer); Type: FUNCTION; Schema: mne_personnal; Owner: admindb
--

CREATE FUNCTION mne_personnal.slotend(p_start integer, p_duration integer, p_slotstart integer, p_slotsize integer, p_slotcount integer) RETURNS integer
    LANGUAGE plpgsql
    AS $$ DECLARE 
    r_slotcount int4;
    r_startslot int4;
    r_start int4;
    r_day int4;
BEGIN
    
    r_start = CAST(FLOOR(EXTRACT(EPOCH FROM date_trunc('day',to_timestamp(p_start)))) AS INTEGER);
    r_startslot = ( p_start - r_start - p_slotstart ) / p_slotsize;
    r_slotcount = ( p_duration + p_slotsize - 1 ) / p_slotsize;
    
    IF ( r_startslot + r_slotcount < p_slotcount ) THEN
      return CAST(FLOOR(EXTRACT(EPOCH FROM to_timestamp(r_start + r_startslot * p_slotsize + p_slotstart) + r_slotcount * p_slotsize * interval '1 sec')) AS INTEGER);
    END IF;
    
    r_slotcount = r_slotcount - p_slotcount + r_startslot;
    r_day = ( r_slotcount - 1 ) / p_slotcount;
    r_slotcount = r_slotcount - ( r_day * p_slotcount );

    return CAST(FLOOR(EXTRACT(EPOCH FROM to_timestamp(r_start ) 
                                         + ( r_day + 1 ) * interval '1 day'
                                         + ( p_slotstart + r_slotcount * p_slotsize ) * interval '1 sec')) AS INTEGER);

END;
 $$;


