CREATE FUNCTION mne_personnal.time_del(pi_timeid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    p_loginname TEXT;
    p_orderproductid TEXT;
    p_orderproducttimeid TEXT;
    p_acttime INTEGER;
 BEGIN
    
    SELECT INTO p_acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    IF NOT FOUND THEN
      SELECT loginname INTO p_loginname from mne_crm.personowndata WHERE personid = pi_userid;
      IF p_loginname <> session_user THEN
        RAISE EXCEPTION '#mne_lang#Kein Zugriff auf fremde Zeitverwaltung#';
      END IF;
    END IF;
         
    DELETE FROM mne_personnal.time WHERE timeid = pi_timeid;
    
    SELECT orderproductid INTO p_orderproductid
    FROM   mne_personnal.orderproducttime
    WHERE orderproducttimeid = p_orderproducttimeid;
    
    UPDATE mne_crm.orderproduct
    SET actcount = ( SELECT
                       CAST( SUM(duration) AS double precision ) / 3600.0
                     FROM 
                       mne_personnal.time
                     WHERE timeid in
                       (
                       SELECT timeid
                       FROM
                         mne_personnal.time t 
                         LEFT JOIN mne_personnal.orderproducttime ot ON ( t.orderproducttimeid = ot.orderproducttimeid)
                         LEFT JOIN mne_crm.orderproduct o ON ( o.orderproductid = ot.orderproductid )
                       WHERE
                         o.orderproductid = p_orderproductid ))
      WHERE
        orderproductid = p_orderproductid AND productunit = E'/h';
    
    RETURN 'ok';
END

 $$;


