    AS $$ DECLARE 
    p_acttime INTEGER;
    p_ctime INTEGER;
    p_cuser VARCHAR;
    p_timeid VARCHAR;
    p_loginname TEXT;
    p_dummy INTEGER;
    p_check BOOLEAN;
    p_orderproductid VARCHAR;
 BEGIN
    
    FOUND := false;
    PERFORM g.rolname as group from pg_catalog.pg_auth_members m, pg_catalog.pg_authid g, pg_catalog.pg_authid u
    WHERE m.roleid = g.oid AND m.member = u.oid AND g.rolname = 'admerppersonnaltime' AND u.rolname = session_user;
    
    IF NOT FOUND THEN
      FOUND := false;
      SELECT loginname INTO p_loginname from mne_crm.personowndata WHERE personid = pi_userid;

      IF NOT FOUND THEN
        RAISE EXCEPTION '#mne_lang#Mitarbeitender hat kein login#';
