      IF p_loginname <> session_user AND session_user <> 'admindb' THEN
        RAISE EXCEPTION '#mne_lang#Kein Zugriff auf fremde Zeitverwaltung#';
      END IF;
      
    END IF;
    
    IF pi_duration < 0 THEN
      RAISE EXCEPTION '#mne_lang#Zeitdauer muss grösser 0 sein#';
