    END IF;

    UPDATE mne_crm.orderproduct
    SET actcount = ( SELECT
                       CAST( COALESCE(SUM(duration),0) AS double precision ) / 3600.0
                     FROM 
                       mne_personnal.time
                     WHERE timeid in
                       (
                       SELECT timeid
                       FROM
                         mne_personnal.time t 
                         LEFT JOIN mne_personnal.orderproducttime ot ON ( t.orderproducttimeid = ot.orderproducttimeid)
                         LEFT JOIN mne_crm.orderproduct o ON ( o.orderproductid = ot.orderproductid )
                       WHERE
                         o.orderproductid = p_orderproductid ))
      WHERE
        orderproductid = p_orderproductid AND productunit = E'/h' AND timeauto = true;

    SELECT o.orderproductid INTO p_orderproductid
    FROM   mne_personnal.time t LEFT JOIN mne_personnal.orderproducttime ot ON ( t.orderproducttimeid = ot.orderproducttimeid) LEFT JOIN mne_crm.orderproduct o ON ( o.orderproductid = ot.orderproductid )
    WHERE t.timeid = p_timeid;
    
    UPDATE mne_crm.orderproduct
    SET actcount = ( SELECT
                       CAST( COALESCE(SUM(duration),0) AS double precision ) / 3600.0
                     FROM 
                       mne_personnal.time
                     WHERE timeid in
                       (
