--

CREATE FUNCTION mne_personnal.timemanagement_mod(pi_timemanagementid character varying, pi_timetyp integer, pi_userid character varying, pi_start integer, pi_duration integer, pi_orderproducttimeid character varying, pi_note character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;

    p_count INTEGER;
    p_slotstart INTEGER;
    p_slotsize INTEGER;
    p_slotcount INTEGER;
    p_duration INTEGER;
    p_end INTEGER;

    result VARCHAR;
 BEGIN
             
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    IF acttime > pi_start THEN
        RAISE EXCEPTION '#mne_lang#Start liegt in der Vergangenheit#';
    END IF;
    
    SELECT mne_personnal.slotend(pi_start, ( pi_duration / slotsize ) * slotsize, slotstart, slotsize, slotcount), 
           ( pi_duration / slotsize ) * slotsize, slotstart, slotsize, slotcount
      INTO p_end, p_duration, p_slotstart, p_slotsize, p_slotcount
    FROM mne_personnal.timemanagement_param;

    SELECT count(timemanagementid) INTO p_count FROM mne_personnal.timemanagement 
    WHERE 
        (
        ( start >=  pi_start AND start < p_end ) OR
        ( mne_personnal.slotend(start, duration, p_slotstart, p_slotsize, p_slotcount ) >  pi_start AND mne_personnal.slotend(start, duration, p_slotstart, p_slotsize, p_slotcount ) <= p_end ) OR
        ( pi_start >= start AND p_end < mne_personnal.slotend(start, duration, p_slotstart, p_slotsize, p_slotcount))
        ) AND
        personid = pi_userid AND timemanagementid != pi_timemanagementid;

    IF p_count > 0 THEN
        RAISE EXCEPTION '#mne_lang#Zeiteintrag überschneidet sich mit einem anderen Zeiteintrag#';
    END IF;    
    
    IF pi_timemanagementid = '################' THEN

      SELECT mne_catalog.mk_id() INTO result;

    ELSE

      result = pi_timemanagementid;
      UPDATE mne_personnal.timemanagement
      SET
        timetyp = pi_timetyp,
        personid = pi_userid,
        orderproducttimeid = NULLIF(pi_orderproducttimeid, ''),
        start = pi_start,
        duration = p_duration,
        note = pi_note,
        modifydate = acttime,
        modifyuser = session_user
      WHERE
        timemanagementid = pi_timemanagementid;

    END IF;
    
    RETURN result;
END

 $$;


