CREATE FUNCTION mne_personnal.timemanagement_vcal(p_timemanagementid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
r RECORD;
BEGIN

  SET LOCAL TIME ZONE 'UTC';
  
  SELECT INTO r
    t3.timemanagementid as uid,
    mne_catalog.epoch_format(t3.start, 'TZ:YYYYMMDD' ) || 'T' || mne_catalog.epoch_format(t3.start, 'HH24MISS' ) as startdate,
    mne_catalog.epoch_format(t3.start + t3.duration, 'TZ:YYYYMMDD' ) || 'T' || mne_catalog.epoch_format(t3.start + t3.duration, 'HH24MISS' ) as enddate,
    t6.description || E'-' || t5.productname AS summary,
    mne_catalog.epoch_format(t3."modifydate", 'YYYYMMDD' ) || 'T' || mne_catalog.epoch_format(t3."modifydate", 'HH24MISS' ) || 'Z' AS lastmodified,
    mne_catalog.epoch_format(t3."createdate", 'YYYYMMDD' ) || 'T' || mne_catalog.epoch_format(t3."createdate", 'HH24MISS' ) || 'Z' AS created
   FROM
    mne_personnal.personowndata t1 LEFT JOIN mne_crm.person t2 ON ( t1.personid = t2.personid) 
    LEFT JOIN mne_personnal.timemanagement t3 ON ( t2.personid = t3.personid) 
    LEFT JOIN mne_personnal.orderproducttime t4 ON ( t3.orderproducttimeid = t4.orderproducttimeid)
    LEFT JOIN mne_crm.orderproduct t5 ON ( t4.orderproductid = t5.orderproductid)
    LEFT JOIN mne_crm.order t6 ON ( t5.orderid = t6.orderid)
   WHERE t3.timemanagementid = p_timemanagementid;
   
   RETURN E'BEGIN:VCALENDAR\r\n' || 
          E'VERSION:2.0\r\n' ||
          E'PRODID:Open Source ERP - Timemanagement\r\n' ||
          E'CALSCALE:GREGORIAN\r\n' ||
          E'BEGIN:VEVENT\r\n' ||
          E'DESCRIPTION:Zeitmanagement - Open Source ERP' || '\n' || r.summary || E'\r\n' ||
