          E'UID:' || r.uid || E'\r\n' ||
          E'LAST-MODIFIED;VALUE=DATE-TIME:' || r.lastmodified || E'\r\n' ||
          E'DTSTAMP;VALUE=DATE-TIME:' || r.lastmodified || E'\r\n' ||
          E'SUMMARY:' || r.summary || E'\r\n' ||
          E'DTSTART;VALUE=DATE-TIME;TZID=' || r.startdate || E'\r\n' ||
          E'DTEND;VALUE=DATE-TIME;TZID=' || r.enddate || E'\r\n' ||
          E'SEQUENCE:0\r\n' ||
          E'TRANSP:TRANSPARENT\r\n' ||
          E'CLASS:PUBLIC\r\n' ||
          E'END:VEVENT\r\n' ||
